We have that
\begin{align}\label{eq: standard interpolation}
\norm{f}_{n,r}\le &\  C \norm{f}^{\frac{l_2}{l_1+l_2}}_{n-l_1,r}\norm{f}^{\frac{l_1}{l_1+l_2}}_{n+l_2,r}
\end{align}
for all $f\in C^{\infty}_0(\overline{B}_r)$, all $l_1, l_2, n\in \N_0$, with $l_1\leq n$, with $C=C(n,l_1,l_2,r)$ depending continuously on $r>0$.

We have that
\begin{align}\label{eq: interpolation t}
    \fnorm{f}_{n,k,r}\le \ C \fnorm{f}^{\frac{l_2}{l_1+l_2}}_{n-l_1,k,r}\fnorm{f}^{\frac{l_1}{l_1+l_2}}_{n+l_2,k,r}
\end{align}
for all $f\in C^{\infty}_0(\overline{B}_r)$, all $k,l_1, l_2, n\in \N_0$, with $l_1\leq n$, with $C=C(n,k,l_1,l_2,r)$ depending continuously on $r>0$.

We have that
\begin{align}\label{eq: interpolation s}
    \fnorm{f}_{n,k,r}\le &\ C \fnorm{f}^{\frac{j_2}{j_1+j_2}}_{n,k-j_1,r}\fnorm{f}^{\frac{j_1}{j_1+j_2}}_{n,k+j_2,r}
\end{align}
for all $f\in C^{\infty}_0(\overline{B}_r)$, all $k,j_1, j_2, n\in \N_0$, with $j_1\leq k$, where the constants $C=C(n,k,j_1,j_2,r)$ depend continuously on $r>0$.

\label{lemma: lambda casimir}
The map defined by
\begin{equation*}
    \begin{array}{ccc}
        C^{\infty}(\R^2_{\ge 0} ) &\to &C_{\sigma,\tau}^{\infty}(\C), \\
         g &\mapsto & \widetilde{g}(x,y):= g(x^2 ,y^2)  
    \end{array}
\end{equation*}
is an isomorphism of rings, and similarly for functions flat at the origin.
