{\cite[\S\phantom{.}73, Ch.\phantom{.}XI]{representation_theory_book}}\label{def.-QL}

	Let $W_0$ be a finite group and $L$ be a $\Z[W_0]$-lattice. Suppose $W_0=\{w_i:i<k\}$ is an enumeration without repetition of $W_0$. Then, $\Q L$ is the $\Q[W_0]$-module with domain $\Q\otimes_\Z L$, and $\Q[W_0]$-scalar multiplication:
	\begin{equation*}
		r.(h\otimes x)=\sum_{i < k}q_ih\otimes w_i.x,
	\end{equation*}
	\noindent
	for all $r\in \Q [W_0]$, $x\in L$, and $h,q_0,\ldots,q_{k-1}\in\Q$ such that $r=\sum_{i<k}q_iw_i$.

{\cite[\S\phantom{.}73, Ch.\phantom{.}XI]{representation_theory_book}}\label{remark - QL}
	In the context of Definition\cspace\ref{def.-QL}, the $\Q[W_0]$-module $\Q L$ naturally inherits the structure of a $\Q$-vector space, where the $\Q$-scalar multiplication is defined by:
	\begin{equation*}
		q.(h\otimes x)=q1.(h\otimes x)=q h\otimes x,
	\end{equation*}
	\noindent
	for all $q,h\in\Q$, and $x\in L$. If $\{x_i:i<n\}$ is a free abelian basis of $L$, then $\{1\otimes x_i:i<n\}$ forms a basis of $\Q L$ as a $\Q$-vector space. In particular, the dimension of $\Q L$ over $\Q$ equals the rank of $L$ as a free abelian group.

{\cite[Section\phantom{.}2]{plesken-pohst}}\label{def. Z/Q-equivalence}

	Let $W_0$ be a finite group, and $L,L'$ be $\Z[W_0]$-lattices. Then, we say that $L$ and $L'$ are:
	\begin{enumerate}[(1)]
		\item $\Z$\emph{-equivalent}, and we write $L\sim_\Z L'$, if\, $L\cong L'$ (as $\Z[W_0]$-lattices);
		\item $\Q$\emph{-equivalent}, and we write $L\sim_\Q L'$, if\, $\Q L \cong \Q L'$ (as $\Q[W_0]$-modules).
	\end{enumerate}

\label{def. - order centerings}
	Let $W_0$ be a finite group, and let $L$ be a $\Z[W_0]$-lattice. We define:
	\begin{enumerate}[(1)]
		\item $\mathcal{C}(L)$ to be the set of all centerings of $L$;
		\item a partial order $\prec$ on $\mathcal{C}(L)$, given by
		\begin{equation*}
			C\prec C' \quad\text{if and only if}\quad \exists \lambda\in\Z\quad\text{such that}\quad C=\lambda C',
		\end{equation*}
		\noindent
		for all $C,C'\in\mathcal{C}(L)$.
	\end{enumerate}

{\cite[Section\phantom{.}2]{plesken-pohst}}\label{lemma - properties order centerings}

	Let $W_0$ be a finite group and $L$ be a $\Z[W_0]$-lattice. Then, the following hold:
	\begin{enumerate}[(1)]
		\item for all $C,C'\in\mathcal{C}(L)$, $C\sim_\Z C'$ whenever $C\prec C'$;
		\item for each $C\in\mathcal{C}(L)$, there exists a unique $\prec$-maximal centering $\overline{C}\in\mathcal{C}(L)$ such that $C\prec \overline{C}$.
	\end{enumerate}

Let $Q$ be a ring (with unity). \mbox{Then a $Q$-module $M$ is said to be:}
	\begin{enumerate}[(1)]
		\item \emph{irreducible} $($or \emph{simple}$)$, if $M$ admits only trivial submodules, i.e., for every submodule $N$ of $M$ either $N=\{0\}$ or $N=M$;
		\item \emph{decomposable} if there exist two non-trivial submodules $N_0$ and $N_1$ of $M$ such that $M=N_0\oplus N_1$;
		\item \emph{indecomposable} if it is not decomposable.
	\end{enumerate}

{\cite[\S\phantom{.}29, Ch.\phantom{.}IV]{representation_theory_book}}\label{def - absolutely irreducible module}
	Let $W_0$ be a finite group and $M$ be a $\Q[W_0]$-module. Suppose that $W_0=\{w_i:i<k\}$ is an enumeration {without repetition of $W_0$.} 
	\begin{enumerate}[(1)]
		\item For every field extension $K$ of $\Q$, we define the $K[W_0]$-module $K M$ as follows\footnote{Various notations are used in the literature: this $K[W_0]$-module is denoted $M^K$ in \cite[\S\phantom{.}29, Ch.\phantom{.}IV]{representation_theory_book}, and $M_{(K)}$ in \cite[\S\phantom{.}8.1, Ch.\phantom{.}II]{bourbaki algèbre}. Here,

		we adopt $K M$ to preserve consistency with Definition\cspace\ref{def.-QL}.}:
		\begin{enumerate}[(i)]
			\item the underlying abelian group is $K\otimes_\Q M$, where the tensor product is computed with respect to the natural $\Q$-vector space structure on $M$;
			\item the $K[W_0]$-scalar multiplication is given by
			\begin{equation*}
				r.\left(\sum_{j<m}b_j\otimes x_j\right)=\sum_{\overset{i<k}{j<m}}a_ib_j\otimes w_i.x_j,
			\end{equation*}
			\noindent
			for all $r\in K [W_0]$, $x_0,\ldots,x_{m-1}\in M$, and $a_0,\ldots,a_{k-1},b_0,\ldots,b_{m-1}\in K$ such that $r=\sum_{i<n}a_iw_i$.
		\end{enumerate}
		\item The module $M$ is said to be \emph{absolutely irreducible} if $K M$ is irreducible for every field extension $K$ of $\Q$.

	\end{enumerate}

\label{theorem}
	Let $W$ be a crystallographic group with translation subgroup $\tranw$ and point group $\pointw$. Suppose that $W'$ is a finitely generated group elementarily equivalent to $W$. If $\lattw$ admits only finitely many $\prec$-maximal centerings, then:
	\begin{enumerate}[(1)]
		\item after identifying $\pointw$ and $\pointw'$ with a common finite group $W_0$, $\lattw\cong\lattwone$ as $\Z[W_0]$-lattices;
		\item if, in addition, $W$ is split, then $W \cong W'$. In particular, $W$ is profinitely rigid.
	\end{enumerate}

\label{def - reflection}
	Let $V$ be a vector space of dimension $l$ over a field $K$ extending $\Q$. Then, a \emph{reflection} in $V$ is an automorphism $s\in\mathrm{GL}(V)$ which is both:
	\begin{enumerate}[(1)]
		\item a \emph{pseudo-reflection}, i.e., the subspace $\mathrm{ker}(1-s)$ has dimension $l-1$;
		\item an \emph{involution}, i.e., $s^2=1$.
	\end{enumerate}
We sometimes say {\em affine reflection}, to stress that we are considering $s \in \mathrm{GL}(V)$.

\label{fact - every involution induces a decomposition of V in ker(1-s)+ker(1+s)}
	Let $V$ be a finitely generated real vector space. Then, every involution $s\in \gl(V)$ induces a decomposition of $V$ as a direct sum of subspaces of the form:
	\begin{equation}\tag{$\star_1$}\label{eq.1 - decomposition V induced by involutions}
		V=\mathrm{ker}(1-s)\oplus\mathrm{ker}(1+s).
	\end{equation}
	\noindent
	When $V$ is Euclidean with inner product $(\,\cdot\,,\,\cdot\,)$ and $s\in O(V)$ is a reflection of the form $s=s_{a,\alpha^*}$, for some $a\in V$ and $\alpha^*\in V^*$, then $s$ admits explicit description as:
	\begin{equation*}
		s(x)=x-2\frac{(x,a)}{(a,a)}a,
	\end{equation*}
	for all $x\in V$ (cf. \cite[\S\phantom{.}2.3, Ch.\phantom{.}V]{bourbaki}, \cite[\S\phantom{.}9.1, Ch.\phantom{.}III]{humphreys_1}), and the decomposition \eqref{eq.1 - decomposition V induced by involutions} simply reduces to:
	\begin{equation*}
		V=H_a\oplus\langle a\rangle_\R,
	\end{equation*}
	\noindent
	with $H_a$ being the hyperplane orthogonal to $a$ in $V$.

