Let $X$ be a set, let $s:X\to S_X$ be a map, and write $s_x:=s(x)$ for all elements $x\in X$. We call the pair $R:=(X,s)$ a \emph{rack} if \begin{equation*}
		s_xs_y=s_{s_x(y)}s_x
	\end{equation*}
        for all $x,y\in X$.  
        If in addition $s_x(x)=x$ for all $x\in X$, then we say that $R$ is a \emph{quandle}.
		The \emph{order} of $R$ is the cardinality of $X$.

\cite[Ex.\ 99]{quandlebook}
		Let $X$ be a set, and fix a permutation $\sigma\in S_X$. Define $s:X\rightarrow S_X$ by $x\mapsto \sigma$, so that $s_x(y)=\sigma(y)$ for all $x,y\in X$. Then $(X,\sigma)\perm:=(X,s)$ is a rack called a \emph{permutation rack}, \emph{cyclic rack}, or \emph{constant action rack}.

        Note that $(X,\sigma)\perm$ is a quandle if and only if $\sigma$ is the identity map $\id_X$. In this case, we call $(X,\id_X)\perm$ a \emph{trivial quandle}.

\cite[Ex.\ 54]{quandlebook}
		Let $G$ be an abelian additive group. Define $s:G\to S_G$ by setting \[s_g(h):=2h-g\] for all elements $g,h\in G$. Then $T(G):=(G,s)$ is a quandle called a \emph{Takasaki kei}. 

        In particular, if $n\in\Z^+$ and $G=\Zn$, then we call $T(\Zn)$ the \emph{dihedral quandle} of order $n$. We denote $T(\Zn)$ by $R_n$.

Let $G$ be a group. We consider several virtual structures on $\Conj G$. Fix an element $g\in G$. 
    \begin{itemize}
        \item Consider the left-multiplication map $m_g:=[h\mapsto gh]$. Then $(\Conj G,m_g)$ is a virtual quandle if and only if $g\in Z(G)$~\cite[Ex.\ 3.6]{bi}.
        \item Consider the conjugation map $c_g:=[h\mapsto ghg\inv]$. Then $(\Conj G,c_g)$ is a virtual quandle~\cite[Lemma 1]{manturov}. More generally, given any rack $R=(X,s)$ and any element $x\in X$, the pair $(R,s_x)$ is a virtual rack.
    \end{itemize}

\label{ex:perm2}
    Let $P=(X,\sigma)\perm$ be a permutation rack. By Example~\ref{ex:perm} and Equation~\eqref{eq:count}, \[v(P)=k(\Aut P)=k(C_{S_X}(\sigma)).\] 

    For example, let $X$ be a finite set of cardinality $n\in\Z^+$. If $P$ is a trivial quandle, then $\Aut P=S_X$, so $v(P)=p(n)$. If, instead, $\sigma\in S_n$ is an $n$-cycle, then $C_{S_X}(\sigma)=\langle\sigma\rangle \cong \Zn$, so $v(P)=n$.

\label{ex:sn}
    Let $n\in\Z^+$ be a positive integer, and let $S_n$ denote the symmetric group on $n$ letters. We compute $v(\Conj S_n)$ as follows. Since $\Conj S_2$ is a trivial quandle, Example~\ref{ex:perm2} yields $v(\Conj S_2)=p(2)=2$. 
    If $n\neq 2$, then $Z(S_n)=1$, so Lemma~\ref{lemma:centerless} implies that
    \[
    \Aut (\Conj S_n)=\begin{cases}
        S_n, &\text{if $n\neq 6$;}\\
        S_6\rtimes \Z/2\Z,&\text{if $n=6$.}
    \end{cases}
    \]
    Since $k(S_n)=p(n)$ for all $n\in\Z^+$ and $k(S_6\rtimes\Z/2\Z)=13$, Equation~\eqref{eq:count} and the above discussion yield \[v(\Conj S_n)=\begin{cases}
        p(n),&\text{if $n\neq 6$;}\\
        13,&\text{if $n=6$.}
    \end{cases}\]

Let $n\in\Z^+$ be a positive integer, and let $A_n$ denote the alternating group on $n$ letters. An argument similar to that of Example~\ref{ex:sn} yields
    \[
    v(\Conj A_n)=\begin{cases}
        p(n),&\text{if $n\neq 2,6$;}\\
        1, & \text{if $n=2$,}\\
        13,&\text{if $n=6$.}
        \end{cases}
    \]

