\label{solutions 2x=g}
    Let 
$G:=\Z_{2^{\alpha_1}}\oplus\Z_{2^{\alpha_2}}\oplus\ldots\oplus\Z_{2^{\alpha_r}}\oplus K $ where $K$ is an abelian group of odd order and $\alpha _i \in \mathbb N$ for each $ i \in [r]$. Fix $g\in G$. Then there are at most $2^r$ solutions in $G$ to the equation $2x=g$.\qed

\label{nb subgroups of index 2 with fixed rank}
Let $r_1$ and $r_2$ be non-negative integers such that $r:=r_1+r_2\in \mathbb N.$ 
    Let $G:=\Z_{2^{\alpha_1}}\oplus\ldots\oplus\Z_{2^{\alpha_{r_1}}}\oplus\Z_2^{r_2}$ with $\alpha_1\geq\ldots\geq\alpha_{r_1}\geq2$. Then there are $2^{r_1}-1$ subgroups of index 2 of $G$ with rank $r$, and $2^r-2^{r_1}$ subgroups of index 2 of $G$ with rank $r-1$.

\cite[Equation (3)]{BLST}\label{dense and regular graphs}
Let $k\geq 1$ and let $\Gamma$ be a graph on $n$ vertices. Suppose that $\Delta(\Gamma)\leq k\delta(\Gamma)$ and set $b:=\sqrt{\delta(\Gamma)}$. Then
$$\mis(\Gamma)\leq \sum_{0\leq i\leq n/b}\binom{n}{i}\cdot3^{\left(\frac{k}{k+1}\right)\frac{n}{3}+\frac{2n}{3b}}.$$

\cite[Corollary 3.3]{BLST2}\label{almosttrianglefree}
    Let $n,D\in\N$ and $k\in\R$. Suppose that $\Gamma$ is a graph and $T$ is a set such that $\Gamma':=\Gamma\setminus T$ is triangle-free. Suppose that $\Delta(\Gamma)\leq D$, $v(\Gamma')=n$ and $e(\Gamma')\geq n/2+k$. Then 
    $$\mis(\Gamma)\leq 2^{n/2-k/(100D^2)+2|T|}.$$

\cite[Lemma 3.5]{ls}\label{stability}
Let $k\in\Z$, $\Delta\in\N$, and $C:= 3^{\Delta/13}$. If $\Gamma$ is an $n$-vertex graph with $n+k$ edges and maximum degree $\Delta$, then
$$\mis(\Gamma)\leq C\cdot 3^{\frac{n}{3}-\frac{k}{13\Delta}}.$$

For subsets $B,S\subseteq G$, let $L^{\star}_S[B]$ be the \emph{distinct link graph of $S$ on $B$} defined as follows. Its vertex set is $B$ and its edge set consists of the following edges:

\begin{enumerate}[label = (\roman*),itemsep=0pt]
    \item two distinct vertices $x,y\in B$ are adjacent if there exists $s\in S$ such that $\{x,y,s\}$ is a distinct Schur triple;
    \item there is a loop at a vertex $x\in B$ if there exist distinct $s,s'\in S$  such that $\{x,s,s'\}$ is a distinct Schur triple.
\end{enumerate}
We call an edge $xy$ in $L^{\star}_S[B]$ a \emph{type 1} edge if $x-y=s$ for some $s\in S\cup (-S)$, and a \emph{type 2} edge if it is not a type 1 edge and $x+y=s$ for some $s\in S$. We denote by $d_i(x,L^{\star}_S[B])$ the number of type $i$ edges incident to $x$ in $L^{\star}_S[B]$. We write $e_i(L^{\star}_S[B])$ for the number of types $i$ edges in $L^{\star}_S[B]$.

\label{degree distinct link graph} Let $x\in A$. Then,
    \begin{itemize}
        \item $d_1(x,\Gamma)=|(S\cup-S)\setminus \{0\}|$;
        \item $d_2(x,\Gamma)\leq |S|$;
        \item $\Gamma $ does not contain any loops.
    \end{itemize}
    In particular, 
    $$|(S\cup-S)\setminus \{0\}|\leq \delta(\Gamma)\leq \Delta(\Gamma)\leq |(S\cup-S)\setminus \{0\}| + |S|.$$
    Consequently,
    \begin{enumerate}
        \item If $0\in S$ and $S=-S$, then $\Delta(\Gamma)\leq 2\delta(\Gamma)+1$.
        \item Otherwise, $\Delta(\Gamma)\leq 2\delta(\Gamma)$.
    \end{enumerate}

