\cite[Theorem 3.1]{Oyono-BC-extensions}\label{OO-thm}
	Let $1\rightarrow N \rightarrow \Gamma \rightarrow \Gamma / N \rightarrow 1$ be an extension of discrete countable groups. Assume that
	\begin{enumerate}
		\item \label{OO-thm-1} every subgroup of $\Gamma$ containing $N$ as a subgroup of finite index satisfies the Baum-Connes conjecture with coefficients;
		\item \label{OO-thm-2} the group $\Gamma / N$ satisfies the Baum-Connes conjecture with coefficients.
	\end{enumerate}
	Then the group $\Gamma$ satisfies the Baum-Connes conjecture with coefficients.

(see Theorem \ref{main-thm})\label{Introduction-thm-1}
	Let $1\rightarrow N \rightarrow \Gamma \rightarrow \Gamma / N \rightarrow 1$ be an extension of discrete countable groups and $A$ be a $C^{\ast}$-algebra equipped with a $\Gamma$-action. Assume that
	\begin{enumerate}
		\item the group $N$ satisfies the Baum-Connes conjecture with coefficients in $A$;
		\item the group $\Gamma / N$ satisfies the Baum-Connes conjecture with coefficients in $C_0(\Gamma / N, A)\rtimes_r \Gamma$.
	\end{enumerate}
	Then the group $\Gamma$ satisfies the Baum-Connes conjecture with coefficients in $A$.

	In particular, if $N$ and $\Gamma / N$ satisfy the Baum-Connes conjecture with coefficients, then so does $\Gamma$.

(see Corollary \ref{main-cor1})\label{Introduction-cor-1}
	Let $N$ be a normal subgroup of $\Gamma$. Assume that
	\begin{enumerate}
		\item the group $N$ satisfies the Baum-Connes conjecture;
		\item the group $\Gamma / N$ satisfies the Baum-Connes conjecture with coefficients in $C_0(\Gamma / N) \rtimes_r \Gamma$.
	\end{enumerate}
	Then the group $\Gamma$ satisfies the Baum-Connes conjecture.

\label{Def-Gamma-C}
Let $\Gamma$ be a discrete countable group. 
  \begin{enumerate}
  	\item A proper metric space $X$ is called to be a \textit{$\Gamma$-space}, if there exists a proper, co-compact and isometric right $\Gamma$-action on $X$.
    \item A $C^{\ast}$-algebra $A$ is called to be a \textit{$\Gamma$-$C^{\ast}$-algebra}, if there exists a $\Gamma$-action on $A$ by $\ast$-automorphisms. 
  \end{enumerate}

\label{Def-prop}
	For $S\subseteq X$, let $\chi_{S}$ be an operator on $_{X}E_A$ defined by $\chi_{S}(\xi\otimes a\otimes h\otimes \eta)=\xi|_{S}\otimes a\otimes h\otimes \eta$. And let $T\in \mathcal{L}(_{X}E_A)$. 
	\begin{enumerate}
		\item The \textit{support} of $T$, denoted by $\supp(T)$, is defined to be
		$$\{(x,x')\in X\times X: \chi_{V} T \chi_{U}\neq 0 \:\: \text{for any open neighborhoods $U$ of $x$, $V$ of $x'$}\}.$$
		\item The \textit{propagation} of $T$, denoted by $\text{prop}(T)$, is defined to be
		$$\text{prop}(T)=\sup\{d(x,x'): (x,x')\in \supp(T)\}.$$  
		\item $T$ is called to be \textit{locally compact}, if $\chi_K T, T\chi_K\in \mathcal{K}(_{X}E_A)$ for any compact subset $K\subseteq X$.
		\item $T$ is called to be \textit{$\Gamma$-invariant}, if $U_{\gamma} T U_{\gamma^{-1}}=T$ for any $\gamma\in \Gamma$.
	\end{enumerate}

\label{Def-eqRoecoe}
	Let $\Gamma$ be a discrete countable group, $X$ be a $\Gamma$-space and $A$ be a $\Gamma$-$C^{\ast}$-algebra. Define $\mathbb{C}[X, A]^{\Gamma}$ to be the $\ast$-algebra consisting of all $\Gamma$-invariant, locally compact operators with finite propagation.
    The \textit{equivariant Roe algebra with coefficients} in $A$ of $X$, denoted by $C^{\ast}(X, A)^{\Gamma}$, is defined to be the norm closure of $\mathbb{C}[X, A]^{\Gamma}$ in $\mathcal{L}(_{X}E_A)$.

\label{Def-equi-coarsemap}
	Let $X$ and $Y$ be two $\Gamma$-spaces. A map $f: X\rightarrow Y$ is called to be an \textit{equivariant coarse map}, if 
	  \begin{itemize}
	  	\item $f(x\gamma)=f(x)\gamma$ for all $x\in X$ and $\gamma\in \Gamma$;
	  	\item $f^{-1}(K)$ is pre-compact for any compact subset $K\subseteq Y$;
	  	\item for any $R>0$, there exists $S>0$ such that $d(f(x), f(x'))\leq S$ for any $x,x'\in X$ with $d(x,x')\leq R$.
	  \end{itemize}

	  $X$ and $Y$ are called to be \textit{equivariantly coarsely equivalent}, if there exist two equivariant coarse maps $f: X\rightarrow Y$, $g: Y\rightarrow X$ and a constant $C>0$ such that
	  $$\max\{d(gf(x), x), d(fg(y), y)\}\leq C,$$
	  for all $x\in X$ and $y\in Y$.

\label{Lem-coveringmap}\cite[Theorem 5.2.6]{WillettYu-Book}
	Let $X$, $Y$ be two $\Gamma$-spaces and $A$ be a $\Gamma$-$C^{\ast}$-algebra. Assume that $f: X\rightarrow Y$ be an equivariant coarse map. Then $f$ leads to a $\ast$-homomorphism from $C^{\ast}(X, A)^{\Gamma}$ to $C^{\ast}(Y, A)^{\Gamma}$, and hence induces a homomorphism
	  $$f_{\ast}: K_{\ast}(C^{\ast}(X, A)^{\Gamma}) \rightarrow K_{\ast}(C^{\ast}(Y, A)^{\Gamma}).$$

\label{Def-localg}
	Let $X$ be a $\Gamma$-space and $A$ be a $\Gamma$-$C^{\ast}$-algebra. The \textit{equivariant localization algebra with coefficients} in $A$ of $X$, denoted by $C^{\ast}_{L}(X, A)^{\Gamma}$, is define to be the norm closure of the $\ast$-algebra consisting of all bounded and uniformly continuous functions $u: [0,\infty) \rightarrow C^{\ast}(X, A)^{\Gamma}$ such that
	  $$\lim_{t\rightarrow \infty}\prop(u(t))=0.$$

\label{Lem-conticovmap}
	Let $X$, $Y$ be two $\Gamma$-spaces and $A$ be a $\Gamma$-$C^{\ast}$-algebra. Assume that $f: X \rightarrow Y$ be a uniformly continuous, equivariant coarse map. Then $f$ induces a homomorphism
	  $$f_{\ast}: K_{\ast}(C^{\ast}_{L}(X,A)^{\Gamma}) \rightarrow K_{\ast}(C^{\ast}_{L}(Y, A)^{\Gamma}).$$

\label{Def-homotopy}
	Let $f^{(0)}, f^{(1)}: X\rightarrow Y$ be two uniformly continuous, equivariant coarse maps. $f^{(0)}$ is called to be \textit{equivariantly homotopic} to $f^{(1)}$, if there exists a uniformly continuous, equivariant coarse map $h: X\times [0,1]\rightarrow Y$ such that $h(x,0)=f^{(0)}(x)$ and $h(x,1)=f^{(1)}(x)$ for all $x\in X$.

	$X$ is called to be \textit{equivariantly homotopy equivalent} to $Y$, if there exist two uniformly continuous, equivariant coarse maps $f_1: X\rightarrow Y$ and $f_2: Y\rightarrow X$ such that $f_2f_1$ and $f_1f_2$ are equivariantly homotopic to $id_{X}$ and $id_{Y}$, respectively.

\label{Lem-equiMV}
	If $\{X_1, X_2\}$ is a cover of $X$ by $\Gamma$-invariant closed subsets, then the following Mayer-Vietoris six-term exact sequence holds
	$$\small\xymatrix{
		K_0(C^{\ast}_{L}(X_1\cap X_2, A)^{\Gamma}) \ar[r] & 
		K_0(C^{\ast}_{L}(X_1, A)^{\Gamma})\oplus K_0(C^{\ast}_{L}(X_2, A)^{\Gamma}) \ar[r] & 
		K_0(C^{\ast}_{L}(X, A)^{\Gamma})\ar[d] \\
		K_1(C^{\ast}_{L}(X, A)^{\Gamma})\ar[u] &
		K_1(C^{\ast}_{L}(X_1, A)^{\Gamma})\oplus K_1(C^{\ast}_{L}(X_2, A)^{\Gamma})\ar[l] &
		K_1(C^{\ast}_{L}(X_1\cap X_2, A)^{\Gamma}).\ar[l] 
	}$$

\label{Def-length}
	Let $\Gamma$ be a discrete countable group. A \textit{length function} on $\Gamma$ is a function $|\cdot|: \Gamma \rightarrow \mathbb{N}$ such that
	\begin{itemize}
		\item $|\gamma|=0$ if and only if $\gamma$ is the identity element;
		\item $|\gamma^{-1}|=|\gamma|$ for any $\gamma\in \Gamma$;
		\item $|\gamma_1 \gamma_2|\leq |\gamma_1|+|\gamma_2|$ for any $\gamma_1, \gamma_2\in \Gamma$.
	\end{itemize}
	A length function $|\cdot|$ on $\Gamma$ is called to be \textit{proper}, if $\{\gamma: |\gamma|\leq R\}$ is finite for any $R\geq 0$.

\cite[Theorem B.3]{GWY-2024}\label{Prop-BCC-Orig}
	Let $\Gamma$ be a discrete countable group and $A$ be a $\Gamma$-$C^{\ast}$-algebra. Then we have the following natural commutative diagram
	 $$\xymatrix{
	 	KK^{\Gamma}_{\ast}(P_{d}(\Gamma), A) \ar[r]^{\mu} \ar[d] & 
	 	K_{\ast}(A \rtimes_{r} \Gamma) \ar[d] \\
	    K_{\ast}(C^{\ast}_{L}(P_{d}(\Gamma), A)^{\Gamma}) \ar[r]^{e_{\ast}} &
	 	K_{\ast}(C^{\ast}(P_{d}(\Gamma), A)^{\Gamma})
	 }$$
	 and the above two vertical maps are all isomorphisms for any $d\geq 0$. Hence, the Baum-Connes assembly map $\mu$ is an isomorphism if and only if Conjecture \ref{BCC} holds.

\label{Def-prop-along}
	For $T\in \mathcal{L}(_{X\times Y} E_A)$, the \textit{propagation along $X$} of $T$, denoted by $\prop_{X}(T)$, is defined to be
	$$\prop_{X}(T)=\sup\{d(x,x'): \:\: \text{there exist $y,y'\in Y$ such that $\left(\left(x,y\right), \left(x',y'\right)\right)\in \supp(T)$}\}.$$

\label{Def-Localg-along}
	The \textit{equivariant localization algebra along $X$ with coefficients} in $A$ of $X\times Y$, denoted by $C^{\ast}_{L, X}(X\times Y, A)^{\Gamma\times G}$, is defined to be the norm closure of the $\ast$-algebra consisting of all bounded and uniformly continuous functions $u:[0,\infty)\rightarrow C^{\ast}(X\times Y, A)^{\Gamma\times G}$ such that
	$$\lim_{t\rightarrow \infty} \prop_{X}(u(t))=0.$$

\label{main-thm1}
    Let $\Gamma$ and $G$ be two discrete countable groups, $A$ be a $\Gamma\times G$-$C^{\ast}$-algebra. If 
    \begin{enumerate}
    	\item $G$ satisfies the Baum-Connes conjecture with coefficients in $A$;
    	\item $\Gamma$ satisfies the Baum-Connes conjecture with coefficients in $A\rtimes_r G$.
    \end{enumerate} 
    Then, $\Gamma\times G$ satisfies the Baum-Connes conjecture with coefficients in $A$.

\label{Cor-app-no-coeff}
	Let $\Gamma$ and $G$ be two discrete countable groups. If
	\begin{enumerate}
		\item $G$ satisfies the Baum-Connes conjecture;
		\item $\Gamma$ satisfies the Baum-Connes conjecture with coefficients in the reduced group $C^{\ast}$-algebra $C^{\ast}_{r}(G)$ (with the trivial $\Gamma$-action).
	\end{enumerate}
	Then, $\Gamma\times G$ satisfies the Baum-Connes conjecture.

\label{Def-Morita-eq}
	Two $C^{\ast}$-algebras $B$ and $D$ are called to be \textit{elementarily Morita equivalent}, if there exists a full projection $p$ in the multiplier algebra of $B$ or a full projection $p'$ in the multiplier algebra of $D$ such that $D=pBp$ or $B=p'Dp'$. 
    $B$ and $D$ are called to be \textit{Morita equivalent}, if there exists a chain $B=B_0, B_1, \cdots, B_n=D$ such that $B_{i-1}$ is elementarily Morita equivalent to $B_{i}$ for any $i=1, \cdots, n$.

\label{Lem-other-Rips-N}
	The embedding map $j$ induces an isomorphism on the $K$-theory level
	$$j_{\ast}: \lim_{d\rightarrow \infty} K_{\ast}(C^{\ast}_{L}(P_{d}(N), A)^{N}) \rightarrow \lim_{d\rightarrow \infty} K_{\ast}(C^{\ast}_{L}(P^{N}_{d}(\Gamma), A)^{N}).$$

\label{Prop-N-loc-Gamma}
	The map $\widetilde{\Psi}$ induces an isomorphism
	$$\widetilde{\Psi}_{\ast}: \lim_{d\rightarrow \infty} K_{\ast}(C^{\ast}_{L}(P^{N}_{d}(\Gamma), A)^{N}) \rightarrow \lim_{d\rightarrow \infty} K_{\ast}(C^{\ast}_{L}(P_d(\Gamma), \Ind^{\Gamma}_{N}A)^{\Gamma}).$$

\label{Lem-BC-sub-group}
	The following two statements are equivalent:
	\begin{enumerate}
		\item $\Gamma$ satisfies the Baum-Connes conjecture with coefficients in $A$;
		\item $\Gamma \times \Gamma/ N$ satisfies the Baum-Connes conjecture with coefficients in $C_0(\Gamma/ N, A)$.
	\end{enumerate}

\label{Lem-BC-quotient}
	If the following two conditions hold:
	\begin{enumerate}
		\item $\Gamma$ satisfies the Baum-Connes conjecture with coefficients in $C_0(\Gamma / N, A)$;
		\item $\Gamma / N$ satisfies the Baum-Connes conjecture with coefficients in $C_0(\Gamma / N, A) \rtimes_r \Gamma$.
	\end{enumerate}
	Then, the Baum-Connes conjecture with coefficients in $C_0(\Gamma / N, A)$ holds for $\Gamma \times \Gamma / N$.

\label{Lem-BC-N-Gamma}
	The following two statements are equivalent:
	\begin{enumerate}
		\item $N$ satisfies the Baum-Connes conjecture with coefficients in $A$;
		\item $\Gamma$ satisfies the Baum-Connes conjecture with coefficients in $C_0(\Gamma / N, A)$.
	\end{enumerate}

\label{main-thm}
	Let $N$ be a normal subgroup of $\Gamma$ and $A$ be a $\Gamma$-$C^{\ast}$-algebra. If the following two conditions hold:
	\begin{enumerate}
		\item the group $N$ satisfies the Baum-Connes conjecture with coefficients in $A$;
		\item the group $\Gamma / N$ satisfies the Baum-Connes conjecture with coefficients in $C_0(\Gamma / N, A) \rtimes_r \Gamma$.
	\end{enumerate}
	Then the group $\Gamma$ satisfies the Baum-Connes conjecture with coefficients in $A$.

\label{main-cor1}
	Let $N$ be a normal subgroup of $\Gamma$. If the following two conditions hold:
	\begin{enumerate}
		\item the group $N$ satisfies the Baum-Connes conjecture;
		\item the group $\Gamma / N$ satisfies the Baum-Connes conjecture with coefficients in $C_0(\Gamma / N) \rtimes_r \Gamma$.
	\end{enumerate}
	Then the group $\Gamma$ satisfies the Baum-Connes conjecture.
