\begin{enumerate}
    \item Let $G=S_n$. The prime simplices of $\Pi(G)$ consist of all subsets of distinct primes $\{p_1,p_2\dots, p_k\}$ with $p_1+p_2+ \dots +p_k\leq n$.
    \item If $G$ is nilpotent, then $\Pi(G)$ consists of all subsets of $\pi(G)$.
    \item For a finite group $G$ with $r=\vert \pi(G)\vert,$ $G^r$ consist of all subsets of $\pi(G).$
\end{enumerate}

Let $G$ be a group, $H\leq G$ and $N \lhd G.$ \begin{enumerate}
    \item    If $S$ is a prime simplex in $H$ then it is also a prime simplex in $G.$ 
    \item If $S$ is a prime simplex in $G/N,$ then it is also a prime simplex in $G.$
\end{enumerate}

\label{graph_imps}
Let $G$ be a finite group.
    \begin{enumerate}
        \item If $G$ is recognizable by prime graph, then it is recognizable by prime simplicial complex, and by spectrum.
        \item If $G$ is unrecognizable by spectrum, then it is unrecognizable by prime simplicial complex, and by prime graph.
    \end{enumerate}
    \begin{proof}
The first result follows directly from the fact that the element orders encoded in the prime graph are contained in both the prime simplicial complex and spectrum for $G$. The second result is the contrapositive of the first.
\end{proof}

\label{centralizerproof}
    Let $G$ be finite group, $p$ be a prime divisor of $G$ and $S$ be a prime simplex of $G$ not containing $p.$ Then the following are equivalent. 
    \begin{enumerate}
        \item $\{p\} \cup S$ is a maximal simplex of $G$
        \item There exists an element $g_p$ or order $p$ such that $\{p\}\cup S$ is a maximal simplex of $C_G(g_p).$
    \end{enumerate}

[{\cite[Proposition 1]{vasil2005connection}}]
\label{vas_indep_set}
 Let $G$ be a finite group and let $\mathrm{Sol}(G)$ be the solvable radical of $G$. For every subset $\rho\subseteq\pi(G)$  with $|\rho|\geq 3$ that are pairwise non-adjacent in $\Gamma(G)$, the intersection $\rho \cap \pi(\mathrm{Sol}(G))$ contains at most one prime.

[{\cite[Lemma A.1(i)]{BG}}]
\label{mult_order_lemma}
    Let $p\neq r$ be a prime and let $i$ be the smallest integer such that $r\mid q^i-1.$ Then $r$ divides $q^m-1$ if any only if $i$ divides $m.$

[Zsigmondy's Theorem]\label{zsigmondy}
 Let $1\leq b < a$ such that $(a,b)=1$ and $n\in \mathbb{N}.$ Then $a^n-b^n$ has a primitive prime divisor unless one of the following holds. \begin{enumerate}
     \item $a=2,$ $b=1,$ $n=1,6$,
     \item \label{zsig_square} $n=2,$ $a+b=2^m$ for some $m\geq 1.$
 \end{enumerate}

[{\cite{MR2076124}}]
    \label{2-3prime}
Suppose $n$ and $n+1$ are consecutive prime powers. Then one of the following holds.
\begin{enumerate}
    \item $(n,n+1) = (2^k,2^k+1)$ for some $k\geq 1$ a power of two and $n+1$ is a \textit{Fermat} prime.
    \item $(n,n+1) = (2^l-1,2^l)$ for some prime $l \geq 3$, and $n$ is a \textit{Mersenne} prime.
\item $(n,n+1)=(8,9)$.
\end{enumerate}

[{\cite{MR50615}}]
\label{primes}
For every integer $n$, there is a prime strictly between $n$ and $2n$. Moreover, if $n\geq 25$, there is a prime strictly between $n$ and $6n/5$.

\label{semi}
Let $G$ be a finite group with Fitting subgroup $F(G)$. Suppose $G$ contains an element of order $pr$, with $p,r$ primes. Then without loss, $p \mid |G/F(G)|$ and one of the following holds.
\begin{enumerate}
    \item $G/F(G)$ contains an element of order $pr$.
    \item  $G/F(G)$ admits an irreducible module in characteristic $r$ such that an element of order $p$ in $G/F(G)$ fixes a non-zero vector. 
\end{enumerate}

[{\cite[Theorem 2]{MR3395066}}]
\label{solv_prime_graphs}
An unlabelled graph $\Gamma$ is isomorphic to the prime graph of a solvable group if and only if the complement $\bar{\Gamma}$ is 3-colourable and triangle-free.

\label{unrec_sol_rad}
    Let $G$ be a finite group. If $G$ is unrecognisable by prime simplicial complex, then either
    \begin{enumerate}
        \item \label{unrec_cond_1} there exists a group $H$ with non-trivial solvable radical with $\Pi(G) = \Pi(H)$, or
        \item The vertex 2 is adjacent to all other primes in $\Gamma(G)$ and $\Pi(G)$ has an odd maximal simplex.
    \end{enumerate}
Moreover, if \ref{unrec_cond_1} holds, then $G$ is unrecognisable by prime simplicial complex.

