Let $\lambda = \exp(z)$.  Here is an estimate we will call on multiple times. It is contingent on the exponent $\mu_1$ of the first power of $s$ in the  normal form \eqref{normal form of u_0} for $u_0$:
\begin{equation} \label{lambda estimate}
 \mu_1 \neq 0 \ \implies \ |\lambda| \  \leq \ \max \set{|x_1|, |y_1|} \ \leq \ 2n,
 \end{equation}   
To establish this, let $\mu'_1$ be exponent of the first power of $s$ in the normal form of $u_m$.  Then  $\mu'_1 = 2^{\lambda}{\mu_1}$ and, by Lemma~\ref{length detail for L},  $2^{\lambda}{\mu_1} \leq 2^{|y_1|}$.  So   $\lambda \leq |y_1|$ because $\mu_1  \in \Z \smallsetminus \set{0}$.  Also  $2^{-\lambda} {\mu}' = {\mu}$ and ${\mu}' \in \Z \smallsetminus \set{0}$, so likewise,  $-\lambda \leq |x_1|$. So  \eqref{lambda estimate} follows, with   \eqref{u2} giving the final inequality. 


We will argue bounds on $|w|$ in cases according to details of the normal form \eqref{L normal form} of $u_0$. 



\begin{enumerate}[label=\arabic*.]    

\item \label{Case 1} \emph{Case: $u_0 = s^{\mu_1}$ for some $\mu_1 \in \Z$.}   

\begin{enumerate}[label=\alph*.]
\item \emph{Subcase: $w_0 = \tilde{w}_0$ (as words).}  
If $\mu_1=0$, then $u_0 =u_m=1$ and $z$ is the empty word, for otherwise we could remove it and  $wx = y  w$ in $G_4$ would remain true.  So,     $|w| = |w_0 w_1|  = |w_0| + |w_1|    \leq 2n$ by \eqref{u1}.  

Assume, then, that $\mu_1 \neq 0$.  Then for $\lambda := \exp(z)$, we have $|\lambda| \leq 2n$ by \eqref{lambda estimate}, and $$u_m \ = \ z u_0 z^{-1} \ = \ z s^{\mu_1} z^{-1} \ = \  s^{2^{\lambda} \mu_1} \ = \ a^{\lambda} s^{\mu_1} a^{-\lambda}.$$ So we may take $z = a^{\lambda}$ because doing so could only shorten $w$ and it will remain true that $wx=yw$ in $G_4$.  So,  using \eqref{u1}, we get $|w| = |w_0 a^{\lambda} w_1|  = |w_0| + |w_1| + |\lambda|   \leq 3n$.    


\item \label{Case 1b} \emph{Subcase: there is some non-empty word $\tau$ such that $\tilde{w}_0  = w_0 \tau$ (as words).}   
This is the situation illustrated in Figure~\ref{vK diagram with corridor block}.  We have $z = \tau^{\ell} \tau_0$ (as words) for some $\ell \geq 0$ and some proper (perhaps empty) prefix $\tau_0$ of $\tau$.    
Let $e = \exp(\tau)$.  

\begin{enumerate}[label=\roman*.]
\item \label{Case 1bi} \emph{Assume $e =0$ or $\mu_1 =0$.} Define $w' := w_0 \tau_0 w_1$. We claim that $w'x=yw'$.  If $\mu_1 =0$, then $u_0 = u_1 = \cdots = u_m$.  If $e=0$, then $\tau^{-1} u_m  \tau = u_m$ and so $u_{|\tau_0|} = \tau^{-\ell} u_m     \tau^{\ell} = u_m$.  Either way, removing the region whose boundary is labelled by $zu_{|\tau_0|}z^{-1}u_m^{-1}$  from the van~Kampen diagram of Figure~\ref{vK diagram with corridor block} and identifying the path labelled by $u_m$  with that labelled by $u_{|\tau_0|}$ demonstrates that $w'x=yw'$.  So, because $|w|$ is minimal, $\ell=0$ and $w  \ = \  w_0  \tau_0 w_1$.  But  $| w_0  \tau_0| \leq | w_0  \tau| \leq |y|$ and $|w_1| \leq |x|$, so $|w| \leq n$.   

\item \emph{Assume, instead, that $e \neq 0$ and $\mu_1 = 0$.} Let $\lambda := \exp(z)$. Then $|\lambda| \leq 2n$ because $\mu_1 \neq 0$ means \eqref{lambda estimate} applies.  But $\lambda = e \ell + \exp(\tau_0)$, so, because $e \neq 0$ and      $|\exp(\tau_0)| \leq |\tau| \leq n$, we deduce that $|\ell| \leq 3n$. So  $$|w|  \ = \  | w_0  \tau^{\ell} \tau_0 w_1|  \ \leq \  | w_0 | + |w_1|  + (\ell+1) |\tau|  \  \leq  \  n +  (3n+1) n   \ = \  3n^2 + 2n.$$
\end{enumerate}



\item \emph{Subcase: there is some non-empty word $\tau$ such that   $w_0  = \tilde{w}_0 \tau$ (as words).}   
We  argue that  $|w| \leq n$  or that  $|w| \leq 3n^2 + 2n$ like in the prior subcase, mutatis matantis.  

\end{enumerate}









\item \emph{Case, $u_0 =  c^{\gamma_0} s^{\mu_1}   c^{\gamma_1} \cdots s^{\mu_{k}}   c^{\gamma_{k}}$ with $\mu_1 \neq 0$ and  ($\gamma_0 \neq 0$ or $\gamma_1 \neq 0$).} 
 In this case all the normal form words $u_0, \ldots, u_m$ contain at least one $c$-letter and so there are no $d^{\pm 1}$ letters in $z$ and we are in the $z = a^{\lambda}$ case of Lemma~\ref{block}.  So, by \eqref{u1} and \eqref{lambda estimate},  $|w| =  | w_0 | + |z| + |w_1|    \leq  n + |\lambda| \leq 3n$.  



\item \label{Case 3} \emph{Case, $u_0 = b^{\beta_0} c^{\gamma_0} \neq 1$.}   
Lemma~\ref{block} applies and we divide into subcases accordingly.  

\begin{enumerate}[label=\alph*.]
\item \label{Case 3a} \emph{Subcase, $z = a^{\lambda_2}d^{\xi} a^{\lambda_1}$ with $\xi \neq 0$.} 

\begin{enumerate}[label=\roman*.]
\item \emph{Assume $w_0 = \tilde{w}_0$ (as words).}   Due to the absence of $s$-letters in $u_0$, $u_{|\lambda_1|} = u_{|\lambda_1|+|\xi|}$ and   $\xi=0$, because otherwise we could shorten $w$.  So this case does not arise. 

\item  \label{Case 3aii} \emph{Assume, instead, that there is some non-empty word $\tau$ such that $\tilde{w}_0  = w_0 \tau$ (as words).}  
Then $z = \tau^{\ell} \tau_0$ (as words) for some $\ell \geq 0$ and some proper (perhaps empty) prefix $\tau_0$ of $\tau$.  There are now two possibilities.  The first is that  $a^{\lambda_2}d^{\xi}$ is a prefix of $\tau$, in which case $|z| \leq 2|\tau| \leq 2n$, and so    $|w| =  | w_0 | + |z| + |w_1|    \leq 4n$.  
The second is that $z = d^{\xi}$ (as words), but then $\gamma_0 =0$ and $u_0 = \cdots =u_m = b^{\beta_0}$,  and like in Case~\ref{Case 1}\ref{Case 1b}\ref{Case 1bi}, $\ell=0$,  $w  \ = \  w_0  \tau_0 w_1$, and $|w| \leq n$.     



\item \emph{Assume, instead, that there is some non-empty word $\tau$ such that $w_0  = \tilde{w}_0 \tau$ (as words).}  
The same argument applies, mutatis mutantis.  
\end{enumerate}


\item \emph{Subcase, $z = a^{\lambda}$.}  In this event, $m = |\lambda|$  and   $u_m = b^{\beta_0} c^{\gamma_0 + \lambda \beta_0}$.  


\begin{enumerate}[label=\roman*.]
\item \emph{Assume $\beta_0 = 0$.}  Then $m= \lambda=0$, and  $u_0 = \cdots =u_m = c^{\gamma_0}$,  and like in Cases~\ref{Case 1}\ref{Case 1b}\ref{Case 1bi} and \ref{Case 3}\ref{Case 3a}\ref{Case 3aii}, $\ell=0$,    $w  \ = \  w_0  \tau_0 w_1$, and $|w| \leq n$.     

\item \emph{Assume, instead, that  $\beta \neq 0$.}  We can use the bounds    $|\gamma| \leq |x_1|^2$ and $|\gamma_0 + \lambda \beta_0| \leq |y_1|^2$ from Lemma~\ref{length detail for L}, to get that $m= |\lambda| \leq |\lambda| |\beta_0| \leq |\gamma_0| +   |y_1|^2 \leq  |x_1|^2 +   |y_1|^2$.  Finally,   $|w|   \leq  n + (2 n)^2  \leq 5n^2$ by \eqref{u1} and \eqref{u2}.   
\end{enumerate}
\end{enumerate}


\item \emph{Case, $u_0 = b^{\beta_0} c^{\gamma_0} s^{\mu_1} b^{\beta_1} c^{\gamma_1} \cdots s^{\mu_{k}} b^{\beta_{k}} c^{\gamma_{k}}$ with $\mu_1 \neq 0$ and $\beta_i \neq 0$ for some $i$.}   

Lemma~\ref{block} applies and tells us that $z = a^{\lambda_2}d^{\xi} a^{\lambda_1}$ for some $\lambda_1, \xi, \lambda_2 \in \Z$.      

We have $\gamma_i = - \beta_i \lambda_1$ and $\gamma'_i =  \beta_i \lambda_2$.  Lemma~\ref{length detail for L}  applies to $x_1 =u_0$ and to $y_1 =u_m$ and gives us that $\gamma_i \leq |x_1|^2$ and $\gamma'_i \leq |y_1|^2$.  So, because $\beta_i \neq 0$, we learn that    $|\lambda_1| \leq  |x_1|^2$ and  $|\lambda_2| \leq |y_1|^2$. 

Now, the exponent   of the first power  of $s$ in the normal form of   $u_m$ is   $2^{\lambda_1 + \xi + \lambda_2} \mu_{1}$.  By  Lemma~\ref{length detail for L}, $|2^{\lambda_1 + \xi + \lambda_2} \mu_{1}| \leq 2^{|y_1|}$. And, because $\mu_1 \neq 0$, we deduce that  $|\lambda_1 + \xi + \lambda_2| \leq |y_1|$.  So $|\xi| \leq |y_1| +  |\lambda_1| + |\lambda_2| \leq |y_1| +  |x_1|^2 + |y_1|^2$ and $$|z| \  = \  |\lambda_1| + |\xi| + |\lambda_2| \ \leq \ |y_1| +  2|x_1|^2 + 2|y_1|^2 \ \leq \ n + 8n^2.$$    
Finally,   $|w|  \leq  n + |z| \leq  2n + 8n^2$ by \eqref{u1} and \eqref{u2}. 
\end{enumerate}
