\label{thmintroIsop}
		Assume that there exists either a $(\varphi,L^0)$-integrable ME-coupling, or an injective\footnote{Here injective means that we require that for a.e.\ $x\in X_H$, the map $\alpha(\cdot, x):G\to H$ is injective.} $\varphi$-integrable measure subgroup coupling from $G$ to $H$.  Then 
		\begin{itemize}
			\item if $\varphi(t)=t$ then \[j_{G}\succcurlyeq j_{H};\]
			\item if $\varphi$ and $t\mapsto t/\varphi(t)$ are increasing then
			\[
			j_{G}\succcurlyeq \varphi\circ j_{H}.
			\]
		\end{itemize}

\label{thm:ME}
Assume that  the sequence of maps $\uu_n\colon \mathcal{G}_n\to \mathcal{H}_n$ is $\mathcal U$-statistically a coarse equivalence. Then there exists an ME coupling from $G$ to $H$. Moreover,
\begin{itemize}
\item if there exists $C>0$ such that every $\uu_n$ has $C$-dense image, then the coupling is cobounded from $G$ to $H$; 
\item  if there exists $C>0$ such that every $\uu_n$ has point-preimages of diameter at most $C$, then the coupling is cobounded from $H$ to $G$;
\item if the maps $\uu_n$ are bijective, then the coupling is an OE coupling.
 \end{itemize}

\label{thm:quantSubgroup}
	Let $\varphi\colon \R^+\to \R^+$ be an unbounded non-decreasing map. Assume the maps $\uu_n\colon \mathcal{G}_n\to H$ are injective and satisfy the following:
 for every $s\in S_G$ there exists $\delta>0$ such that
\begin{equation}\label{eq:quantLip}
	\sum_{r=0}^\infty \varphi\!\left(\delta r\right) \lim_\mathcal U\PP_{\mathcal{G}_n}\left(\left\{x\in \mathcal{G}_n\mid d_{\mathcal{H}_n}(\uu_n(x),\uu_n(xs))=r\right\}\right)<\infty;
	\end{equation}
	Then, there exists an injective measure subgroup coupling from $G$ to $H$ which is $\varphi$-integrable.

\label{thm:quantME}
	Let $\varphi,\psi\colon \R^+\to \R^+$ be two unbounded non-decreasing maps. Let $G$ and $H$ be two finitely generated groups with sofic approximations $(\mathcal{G}_n)_n$ and $(\mathcal{H}_n)_n$ and $\uu_n\colon \mathcal{G}_n\to \mathcal{H}_n$ satisfy the following:
	\begin{enumerate}
	\item There exists $C>0$ such that every $\uu_n$ has $C$-dense image and has point-preimages of diameter at most $C$;
	\item For every $s\in S_G$ there exists $\delta>0$ such that (\ref{eq:quantLip}) holds.
	\item For every $h\in H$ of length at most $2C+1$, there exists $\delta>0$ such that
		\begin{equation}\label{eq:quantExp}
	\sum_{r=0}^\infty \psi\!\left(\delta r\right)  \lim_\mathcal U \PP_{\mathcal{H}_n}\left(\left\{y \in \uu_n(\mathcal{G}_n) \mid  \diam_{\mathcal{G}_n}(\uu_n^{-1}(y)\cup \uu_n^{-1}(yh))=r\right\}\right)<\infty.
		\end{equation}
	\end{enumerate}
	Then, there exists a  measure equivalence coupling from $G$ to $H$ which  is $(\varphi,\psi)$-integrable and mutually cobounded (OE if the maps $\uu_n$ are bijective).

Two infinite finitely generated groups $G$ and $H$ are said to be \emph{measure equivalent} if there exists a standard Borel measure space $(\Omega,\mu)$ of infinite measure with commuting measure preserving actions of $G$ and $H$ with measure fundamental domains of finite measure, that is, there exist Borel sets $X_G, X_H \subseteq \Omega$ of finite measure for which
	\begin{itemize}
		\item $\mu(g\ast X_G \cap X_G)= 0$ for all $g \in G \setminus \lbrace e_G\rbrace$ and $\mu(\Omega \setminus G\ast X_G) = 0$;
		\item $\mu(h\ast X_H \cap X_H)= 0$ for all $h \in H \setminus \lbrace e_H\rbrace$ and $\mu(\Omega \setminus H\ast X_H) = 0$.
	\end{itemize}

Let $\{(X_n,\mu_n)\}_{n\in\N}$ be a sequence of finite probability spaces and let $X_{\mathcal{U}}$ be the ultraproduct of the sequence $\{X_n\}_n$. Then there exists a complete probability space $(X_{\mathcal{U}},\mathfrak{B}_{\mathcal{U}},\mu_{\mathcal{U}})$ such that
  \begin{enumerate}
  \item for every sequence $\{A_n\subseteq X_n\}_n$ of subsets, the set $[A_n]_{\mathcal{U}}$ is measurable and $\mu_{\mathcal{U}}([A_n]_{\mathcal{U}})=\lim_{\mathcal{U}}\mu_n(A_n)$,
  \item for every measurable subset $A_{\mathcal{U}}\subseteq X_{\mathcal{U}}$ there exists a sequence of subsets $\{A_n\subseteq X_n\}_n$ such that $\mu_{\mathcal{U}}(A_{\mathcal{U}}\Delta [A_n]_{\mathcal{U}})=0$.
  \end{enumerate}

\label{def:statisticSet}
	Let $(Y_n)_n$ and $(Z_n)_n$ be two sequences of metric spaces and $\uu_n\colon Y_n\to Z_n$ be a sequence of maps.
	\begin{itemize}
		\item Assume that $Y_n$ is finite. We say that the sequence $(\uu_n)_n$ is \emph{$\mathcal{U}$-statistically coarsely injective} if
		\[\lim_{C\to \infty}\lim_\mathcal{U}\PP_{Y_n}\left(\left\{y\in Y_n \mid \diam_{Y_n}(\uu_n^{-1}(\uu_n(y)))\geq C\right\}\right)=0.\]

		\item Assume that $Z_n$ is finite. We say that the sequence $(\uu_n)_n$ is \emph{$\mathcal{U}$-statistically coarsely surjective} if
		\[\lim_{C\to \infty}\lim_\mathcal{U}\PP_{Z_n}\left(\left\{z\in Z_n \mid d_{Z_n}(z,\uu_n(Y_n))\ge C\right\}\right)=0.\]
		\item Assume that $Y_n$ and $Z_n$ are finite.  The sequence $(\uu_n)_n$ is \emph{$\mathcal{U}$-statistically coarsely bijective} if it is both $\mathcal{U}$-statistically coarsely injective and $\mathcal{U}$-statistically coarsely surjective.
	\end{itemize}

\label{prop:compaireSize}
	Let $(Y_n)_n$ and $(Z_n)_n$ be two sequences of uniformly locally finite metric spaces and let $\uu_n\colon Y_n\to Z_n$ be a sequence of maps, then
	\begin{itemize}
		\item If the sequence $(\uu_n)_n$ is $\mathcal{U}$-statistically coarsely surjective, then $\lim_\mathcal{U}\# Z_n/\# Y_n$ is finite.
		\item If the sequence $(\uu_n)_n$ is $\mathcal{U}$-statistically coarsely injective, then  $\lim_\mathcal{U}\# Y_n/\# \Im\uu_n$ (so in particular $\lim_\mathcal{U}\# Y_n/\# Z_n$) is finite.
	\end{itemize}

\label{def:statisticCoarseMetric}
	Let $(Y_n)_n$ and $(Z_n)_n$ be two sequences of finite metric spaces that are uniformly locally finite and $\uu_n\colon Y_n\to Z_n$ be a sequence of maps.
	\begin{itemize}
		\item The sequence $(\uu_n)_n$ is \emph{$\mathcal{U}$-statistically Lipschitz} if for every $r>0$,
		\[\lim_{C\to \infty}\lim_\mathcal{U}\PP_{Y_n}\left(\left\{y\in Y_n\mid \diam_{Z_n}(\uu_n(B_{Y_n}(y,r))\geq C\right\}\right)= 0.\]

		\item The sequence $(\uu_n)_n$ is \emph{$\mathcal{U}$-statistically expansive} if for every $r>0,$

		\[\lim_{C\to \infty}\lim_\mathcal{U}\PP_{Y_n}\left(\left\{y\in Y_n\mid \diam_{Y_n}(\uu_n^{-1}(B_{Z_n}(\uu_n(y),r))\geq C\right\}\right)= 0.\]


		\item The sequence $(\uu_n)_n$ is \emph{$\mathcal{U}$-statistically a coarse equivalence} if it is $\mathcal{U}$-statistically Lipschitz, $\mathcal{U}$-statistically expansive and $\mathcal{U}$-statistically coarsely bijective.	\end{itemize}

\label{prop:expsofic}
Let $G$ and $H$ be two groups each with finite generating set $S_G$ and $S_H$, respectively, and let $(\mathcal{G}_n)_n$ and $(\mathcal{H}_n)_n$ be sequences of sofic approximations of the Cayley graphs $(G,S_G)$ and $(H,S_H)$ respectively.
Let $\uu_n\colon \mathcal{G}_n\to \mathcal{H}_n$ be a sequence of maps.
\begin{enumerate}
	\item\label{item:StatBiLGroup2} If the sequence $\uu_n$ is $\mathcal{U}$-statistically coarsely surjective, then it is $\mathcal{U}$-statistically expansive if and only if for every $h\in H$ we have
	\[\lim_{C\to \infty}\lim_{\mathcal U}\PP_{\mathcal{G}_n}\left(\left\{x\in \mathcal{G}_n\mid \diam_{\mathcal{G}_n}(\uu_n^{-1}(\uu_n(x))\cup\uu_n^{-1}(\uu_n(x)h))\ge C\right\}\right)= 0.\]
	\item\label{item:StatBiLGroup3} If the sequence $\uu_n$  has $C'$-dense images, and  has preimages  of bounded diameter, then it is $\mathcal{U}$-statistically expansive if and only if for every $h\in B_H(e_H,2C'+1)$, 
	\[\lim_{C\to \infty}\lim_{\mathcal U}\PP_{\mathcal{G}_n}\left(\left\{y\in \uu_n(\mathcal{G}_n)\mid \diam_{\mathcal{G}_n}(\uu_n^{-1}(y)\cup\uu_n^{-1}(yh))\ge C\right\}\right)= 0\]
\end{enumerate}

\label{prop:approximatecocycle}
	Assuming 
	\begin{itemize}
	\item either that $\uu_n:\mathcal{G}_n\to H$ is {\bf  $\mathcal{U}$-statistically Lipschitz};  	
	\item or that $\uu_n:\mathcal{G}_n\to \mathcal{H}_n$ is {\bf  $\mathcal{U}$-statistically Lipschitz  and  $\mathcal{U}$-statistically coarsely bijective},
\end{itemize}	
then for every $g,g'\in G$,
	\[\lim_\mathcal{U}\PP_{\mathcal{G}_n}\left(\mathcal{C}_n(g,g')\right)= 1,\]
	and  \[\lim_{R\to \infty }\PP_{\mathcal{G}_n}\left( \left\{|T_n(g,x)|_{S_H}\leq R)\right\}\right)=1.\]

\label{lem:computationsemiring}	
	Let $W_i=W_{\aaa^i,B_i}$ for $i=0,\ldots, m$. Then $\mu(W_0\setminus \bigcup_{i=1}^m W_i)$ is given by
	\begin{multline*}
	\lim_\mathcal{U}\PP_{\mathcal{G}_n}\Big(\Big\{x\in \mathcal{G}_n\mid \exists h\in H \text{ such that }\\
	\forall b\in B_0\colon T_n(b,x)=h\aaa_H^0(b), \;\rho_n(xb)= \aaa^0_\N(b)  \text{ and }\\
	\forall i\in\{1,\ldots,m\}\colon \exists b'\in B_i\colon T_n(b',x)\neq h\aaa^i_H(z) \text{ or } \rho_n(x b')\neq \aaa_\N^i(b')\Big\}\Big).
	\end{multline*}

\label{prop:ME}
	Let $G$ and $H$ be two finitely generated groups with sofic approximations $(\mathcal{G}_n)_n$ and $(\mathcal{H}_n)_n$. Assume that  the  sequence of maps $\uu_n\colon \mathcal{G}_n\to \mathcal{H}_n$ is $\mathcal{U}$-statistically a coarse equivalence. Then, there exists a measure equivalence coupling $(\Omega,\mu)$ between be the groups $G$ and $H$ such that 
	\[\frac{\mu(\Omega/G)}{\mu(\Omega/H)}=\lim_{\mathcal{U}}\frac{\#\mathcal{H}_n}{\#\mathcal{G}_n}.\]	.

\label{prop:fundamentalDomainBis}
	Assume that $\uu_n:\mathcal{G}_n\to \mathcal{H}_n$ are $\mathcal{U}$-statistically Lipschitz  and $\mathcal{U}$-statistically coarsely bijective.
	\begin{itemize}
		\item If in addition the maps $\uu_n$ have fibers with diameter at most $R$, then $\mu(X_H\setminus B_G(e_G,R)*X_G)=0$. In particular, if the maps are  injective, then $\mu(X_H\setminus X_G)=0$.
		\item 
		If the maps $\uu_n$ are $\mathcal{U}$-statistically a coarse equivalence and have $C$-dense image, then $\mu(X_G\setminus B_H(e_H,C)*X_H)=0$. In particular, if the maps are surjective, then $\mu(X_G\setminus X_H)=0$. 
		\item if both conditions are satisfied, then the resulting coupling is mutually cobounded, and if $\uu$ is  bijective, then $X_H=X_G$ a.e.
	\end{itemize}

\label{prop:QuantSubgroup}
	Let $\varphi\colon \R^+\to \R^+$ be a non-decreasing map.
	Let $(\Omega,X_H,\mu)$ be a measure subgroup coupling from $G$ to $H$ as in \cref{thm:ME} and let $\alpha\colon G\times X_H\to H$ be associated cocycle. 
	 Then, $\alpha$ is $\varphi$-integrable if  for every $g\in S_G$ there exists a $\delta>0$ such that
	\begin{equation}\label{eq:UQuantLip}
	\sum_{r=0}^\infty \varphi\!\left(\delta r\right)\lim_{\mathcal{U}} \PP_{ \mathcal{G}_n}\left(\left\{x\in \mathcal{G}_n\mid d_{\mathcal{H}_n}(\uu_n(x),\uu_n(xg^{-1}))=r\right\}\right)<\infty.
	\end{equation}
	Similarly, $\alpha$ is strongly $\varphi$-integrable (or $\varphi^\diamond$-integrable)  if for every $\varepsilon>0$ there exists a $\delta>0$ and $C>0$ such that for every $g \in G$ we have that
	\begin{equation}\label{eq:UQuantLipStrong}
	\sum_{r=0}^\infty\varphi\!\left(\delta r\right)\lim_{\mathcal{U}} \PP_{ \mathcal{G}_n}\left(\left\{x\in \mathcal{G}_n\mid d_{\mathcal{H}_n}(\uu_n(x),\uu_n(xg^{-1}))=r\right\}\right)\le C\varphi(\varepsilon |g|).
	\end{equation}

\label{prop:Cocycle}
Let $\varphi\colon \R^+\to \R^+$ be a non-decreasing map,
let $(\Omega,X_G,X_H,\mu)$ be a measure equivalence coupling from $G$ to $H$ as in \cref{thm:ME} and let $\beta\colon H\times X_G\to G$ be the measure equivalence cocycle given by $h*\aaa\in\beta(h,\aaa)^{-1}*X_G$ for every $h\in H$ and almost every $\aaa\in X_G$.
Suppose there exists a $C>0$ such that the maps $\uu_n$ are $C$-dense.
Then, $\beta$ is $\varphi$-integrable if for every $h\in B_H(e,2C+1)$ there exists a $\delta>0$ such that
	\begin{equation}\label{eq:UQuantExp}
\sum_{r=0}^\infty\varphi(\delta r) \lim_{\mathcal{U}} \PP_{\mathcal{H}_n}\left(\left\{y\in \uu_n(\mathcal{G}_n)\mid \diam_{\mathcal{G}_n}(\uu_n^{-1}(y)\cup \uu_n^{-1}(yh))\geq r\right\}\right)<\infty.
\end{equation}
Similarly, $\beta$ is strongly $\varphi$-integrable (or $\varphi^\diamond$-integrable) if  if for every $\varepsilon>0$ there exists a $\delta>0$ and $C'>0$ such that for every $h\in H$ we have that
	\begin{equation}\label{eq:UQuantExpstrong}
	\sum_{r=0}^\infty \varphi(\delta r)\lim_{\mathcal{U}} \PP_{\mathcal{H}_n}\left(\left\{y\in \uu_n(\mathcal{G}_n)\mid \diam_{\mathcal{G}_n}(\uu_n^{-1}(y)\cup \uu_n^{-1}(yh))\geq r\right\}\right)\le C'\varphi(\varepsilon |h|).
	\end{equation}
Besides, if we assume that the point-preimages of $\uu_n$ has diameter at most $C$, then these two conditions can respectively be relaxed to the following ones\footnote{where the inequality for the diameter is replaced by an equality. Note that if $\varphi$ is a quickly growing function (e.g.\ a stretched exponential), then the two conditions are equivalent, so there is no gain in assuming the bound on the diameter of preimages.}
	\begin{equation}\label{eq:UQuantExpC}
\sum_{r=0}^\infty\varphi(\delta r) \lim_{\mathcal{U}} \PP_{\mathcal{H}_n}\left(\left\{y\in \uu_n(\mathcal{G}_n)\mid \diam_{\mathcal{G}_n}(\uu_n^{-1}(y)\cup \uu_n^{-1}(yh))= r\right\}\right)<\infty.
\end{equation}
and 
	\begin{equation}\label{eq:UQuantExpCstrong}
\sum_{r=0}^\infty \varphi(\delta r)\lim_{\mathcal{U}} \PP_{\mathcal{H}_n}\left(\left\{y\in \uu_n(\mathcal{G}_n)\mid \diam_{\mathcal{G}_n}(\uu_n^{-1}(y)\cup \uu_n^{-1}(yh))= r\right\}\right)\le C'\varphi(\varepsilon |h|).
\end{equation}

\label{rem:Strong}
	In \cref{thm:quantSubgroup} (and \cref{thm:quantME}), we obtain {\bf strong} integrability if we assume the stronger version of (\ref{eq:quantLip}): that for every $\varepsilon>0$ there exists a $\delta>0$ and $C'>0$ such that
	\[\sum_{r=0}^\infty \varphi\!\left(\delta r\right)\lim_{\mathcal U}\PP_{\mathcal{G}_n}\left(\{x\in \mathcal{G}_n\mid d_{\mathcal{H}_n}(\uu_n(x),\uu_n(xg))=r\}\right)\le C'\varphi(\varepsilon|g|)\]
	for every $g\in G$. Similarly the coupling is $(\varphi,\psi^\diamond)$-integrable if for every $\varepsilon>0$ there exists a $\delta>0$ and $C'>0$ such that
	\begin{equation}\label{eq:quantExpStrong}
	\sum_{r=0}^\infty \psi\!\left(\delta r\right)\lim_{\mathcal U} \PP_{\mathcal{H}_n}\left(\{y \in \uu_n(\mathcal{G}_n)\mid \diam_{\mathcal{G}_n}(\uu_n^{-1}(y)\cup\uu_n^{-1}(yh)) = r\}\right)\le C'\psi(\varepsilon |h|)\end{equation}
	for every $h\in H$ and it is $(\varphi^\diamond,\psi^\diamond)$-integrable if both conditions are satisfied.

