(\cite{mordeson_comm}, Theorem 1.1.14)
	\label{hom}
	Let $f : X \rightarrow Y$ be a mapping.
	\begin{enumerate}
		\item[({i})] Let $\{ \mu_i \}_{i \in I}$ be a family of $L$-subsets of $X$. Then, $f(\mathop{\cup}\limits_{i \in I} \mu_i) = \mathop{\cup}\limits_{i \in I} f(\mu_i)$ and $f(\mathop{\cap}\limits_{i \in I} \mu_i) \subseteq \mathop{\cap}\limits_{i \in I}f(\mu_i)$.
		\item[({ii})] Let $\mu \in L^X$. Then, $f^{-1}(f(\mu)) \supseteq \mu$. The equality holds if $f$ is injective.
		\item[{(iii)}] Let  $\nu \in L^Y$. Then, $f(f^{-1}(\nu)) \subseteq \nu$. The equality holds if $f$ is surjective.
		\item[{(iv)}] Let $\mu \in L^X$ and $\nu \in L^Y$. Then, $f(\mu) \subseteq \nu$ if and only if $\mu \subseteq f^{-1}(\nu)$. Moreover, if $f$ is injective, then $f^{-1}(\nu) \subseteq \mu$ if and only if $\nu \subseteq f(\mu)$.
	\end{enumerate}

Let $\mu \in L ^G $. Then, $\mu $ is called an $L$-subgroup of $G$ if for each $x, y\in G$,
	\begin{enumerate}
		\item[({i})] $\mu (xy)\ge \mu (x)\wedge \mu (y)$,
		\item[({ii})] $\mu (x^{-1} )=\mu (x)$.
	\end{enumerate}
	The set of $L$-subgroups of $G$ is denoted by $L(G)$. Clearly, the tip of an $L$-subgroup is attained at the identity element of $G$, that is, $\text{tip}(\mu) = \mu(e)$.

(\cite{mordeson_comm}, Lemma 1.2.5)
	\label{lev_gp}
	Let $\mu \in L ^G $. Then, $\mu $ is an $L$-subgroup of $G$ if and only if each non-empty level subset $\mu_{a} $ is a subgroup of $G$.

(\cite{mordeson_comm}, Theorems 1.2.10, 1.2.11)
	\label{hom_gp}
	Let $f : G \rightarrow H$ be a group homomorphism. Let $\mu \in L(G)$ and $\nu \in L(H)$. Then, $f(\mu) \in L(H)$ and $f^{-1}(\nu) \in L(G)$.

(\cite{ajmal_nil}, Theorem 2.1)
	\label{lev_sgp}
	Let $\eta \in L^\mu$. Then, $\eta\in L(\mu)$ if and only if each non-empty level subset $\eta_a$  is a subgroup of $\mu_a$.

(\cite{wu_normal})
	Let $\eta \in L(\mu)$. Then, we say that  $\eta$  is a normal $L$-subgroup of $\mu$   if  
	\begin{center}
		$\eta(yxy^{-1}) \geq \eta(x)\wedge \mu(y)$ for  all  $x,y \in G.$
	\end{center}

(\cite{mordeson_comm}, Theorem 1.4.3)
	\label{lev_norsgp}
	Let $\eta \in L(\mu)$. Then, $\eta\in NL(\mu)$ if and only if each non-empty level subset $\eta_a$  is a normal subgroup of $\mu_a$.

(\cite{ajmal_gen}, Theorem 3.1)
	\label{gen}
	Let $\eta\in L^{^{\mu}}.$ Let $a_{0}=\mathop {\vee}\limits_{x\in G}{\left\{\eta\left(x\right)\right\}}$ and define an $L$-subset $\hat{\eta}$ of $G$ by
	\begin{center}
		$\hat{\eta}\left(x\right)=\mathop{\vee}\limits_{a \leq a_{0}}{\left\{a \mid x\in\left\langle \eta_{a}\right\rangle\right\}}$.
	\end{center}

	\noindent Then, $\hat{\eta}\in L(\mu)$ and  $\hat{\eta} =\left\langle \eta \right\rangle$.

(\cite{ajmal_gen}, Theorem 3.7)
	\label{gen_sup}
	Let $\eta \in L^{\mu}$ and $a_0 = \mathop{\vee}\limits_{x \in G}\{\eta(x)\}$, then for all $b \leq a_0$, $\langle \eta_b \rangle \subseteq \langle \eta \rangle_b$. Moreover, if $\eta$ possesses the sup-property, then $\langle \eta_b \rangle = \langle \eta \rangle_b$.

(\cite{ajmal_sol}, Lemma 3.27)
	\label{gen_hom}
	Let $f : G \rightarrow H$ be a group homomorphism, let $\mu \in L(G)$ and $\nu \in L(H)$. Then, for all $\eta \in L^{\mu}$, $\langle f(\eta) \rangle = f(\langle \eta \rangle)$ and for all $\theta \in L^{\nu}$, $\langle f^{-1}(\theta) \rangle = f^{-1}(\langle \theta \rangle).$

