Let $X=X_1 \sqcup\cdots\sqcup X_k$ be a finite quandle. Then $X$ is said to be:
\begin{enumerate}
\item  \textit{left $2$-transitive} if the semigroup  $H(X)$ acts $2$-transitively on $X$.
\item  \textit{right $2$-transitive} if the group $\Inn(X)$ acts $2$-transitively on $X$. 
\item  \textit{right orbit $2$-transitive} if  the group $G(X_i)$ acts $2$-transitively on $X_i$ for each $i$.
\end{enumerate}

\label{TransClassif}
Let $X$ be a finite quandle of order $n$ and $\mathbb{k}$ a field. Then the following statements hold:
\begin{enumerate}
\item If $\charac(\mathbb{k})=0$,  then $V_{\textrm{st}}$ is an irreducible representation of the subgroup $G < S_n$ if and only if  $G$ is $2$-transitive and $A_n\not\leq G$. 
 \item If $\charac(\mathbb{k})=p>3$,  then $V_{\textrm{st}}$ is an irreducible representation of the subgroup $G < S_n$ if and only if  $G$ is $2$-transitive and $A_n\not\leq G$ except when:
 \begin{enumerate}
 \item[(i)] $G \leqslant  A\Gamma L(m, q)$ and $p$ divides $q$;
 \item[(ii)] $G \leqslant  P\Gamma L(m, q)$, $m\geq 3$ and $p$ divides $q$;
 \item[(iii)] $G \leqslant  P\Gamma U(3, q)$ and $p$ divides $q+1$;
 \item[(iv)] $G \leqslant  Sz(q)$ and $p$ divides $q+1+m$, where $m^2=2q$;
 \item[(v)] $G \leqslant  Re(q)$ and $p$ divides $(q+1)(q+1+m)$, where $m^2=3q$.
\end{enumerate} 
\end{enumerate}

\label{RightModDecomp}
Let $X$ be a finite quandle of order $n$ such that $A_n \not\le \Inn(X)$ and $\mathbb{k}$ a field. Then the following statements hold:
\begin{enumerate}
\item  If $X=X_1 \sqcup \cdots \sqcup X_k$ is a right orbit 2-transitive quandle and $\charac(\mathbb{k})=0$, then  $\mathbb{k}[X]\cong \underset{i=1}{\overset{k}{\bigoplus}}(V^i_{\textrm{st}}\oplus V^i_{\textrm{triv}})$, where the summands on the right hand side are simple right ideals.

\item  If $X=X_1 \sqcup \cdots \sqcup X_k$ is a right orbit 2-transitive quandle such that none of the groups $G(X_i)$ is in the list of groups in Theorem~\ref{TransClassif}(2) and  $\charac(\mathbb{k})=p>3$, then $\mathbb{k}[X]\cong \underset{i=1}{\overset{k}{\bigoplus}}(V^i_{\textrm{st}}\oplus V^i_{\textrm{triv}})$, where the summands on the right hand side are simple right ideals.

\item If $X$ is such that  $H(X)$ contains a maximal subgroup with a $2$-transitive action on $X$ and $\charac(\mathbb{k})=0$, then $\mathbb{k}[X]\cong V_{\textrm{st}}\oplus V_{\textrm{triv}}$, where the summands on the right hand side are simple left ideals.

\item If $X$ is such that  $H(X)$ contains a maximal subgroup with a $2$-transitive action on $X$, which is not in the list of groups in  Theorem~\ref{TransClassif}(2) and $\charac(\mathbb{k})=p>3$, then $\mathbb{k}[X]\cong V_{\textrm{st}}\oplus V_{\textrm{trivial}}$, where the summands on the right hand side are simple left ideals.
\end{enumerate}

Let $X$ be a finite quandle of order $n$ such that $A_n \not\le \Inn(X)$ and $\mathbb{k}$ a field. Then the following statements hold:
\begin{enumerate}
\item  If $X=X_1 \sqcup\cdots\sqcup X_k$ is such that each subquandle $X_i$ is of right cyclic type, then $\mathbb{k}[X]\cong \underset{i=1}{\overset{k}{\bigoplus}}(V^i_{st}\oplus V^i_{triv})$, where the summands on the right hand side are simple right ideals.
\item If $X$ is of left cyclic type, then $\mathbb{k}[X]\cong V_{st}\oplus V_{triv}$, where the summands on the right hand side are simple left ideals.
\end{enumerate}

\label{ISO}
Let $\mathbb{k}$ be an associative ring with unity such that $\charac(\mathbb{k})\neq 2,3$. Let  $X$ and $Y$ be right orbit $2$-transitive quandles
such that $\mathbb{k}[X] \cong \mathbb{k}[Y]$ and $A_n \not\le \Inn(X)$. Then $X$ and $Y$ are of the same partition type.

\label{two different elements}
The following  assertions hold:
\begin{enumerate}
\item  If $X$ is a quandle containing a trivial subquandle with more than one element, then $\mathbb{k}[X]$ contains zero-divisors.
\item If $X$ is an inert quandle, then $\mathbb{k}[X]$ contains zero-divisors. In particular, if $X$ contains a finite subquandle with more than one element, then $\mathbb{k}[X]$ contains zero-divisors.
\item If $X$ is not semi-latin, then $\mathbb{k}[X]$ contains zero-divisors.
\end{enumerate}

Let $G$ be a bi-orderable group. Then the following assertions hold:
\begin{enumerate}
\item $\mathbb{k}[\Core(G)]$ has no  zero-divisors.
\item If $\phi \in \Aut(G)$ is an order reversing automorphism, then $\mathbb{k}[\Alex(G, \phi)]$ has no  zero-divisors.

\end{enumerate}

\label{auto quandle ring free products}
Let $X$ be a quandle and $\mathbb{k}$ an integral domain with unity. If $\mathbb{k}[X]$ admits only trivial idempotents, then 
$\Aut_{\textrm{algebra}}\big(\mathbb{k}[X]\big) \cong \Aut_{\textrm{quandle}}(X).$

{\rm 
 If a quandle $X = X_1 \cup \cdots \cup X_n$ is a union of subquandles and $\mathbb{k}$ an integral domain with unity, then
\begin{equation}\label{idempotents-union}
  \mathcal{I} \big(\mathbb{k}[X_1] \big) \cup \cdots \cup \mathcal{I} \big(\mathbb{k}[X_n] \big) \subseteq \mathcal{I} \big(\mathbb{k}[X] \big).
\end{equation}
The inclusion is, in general, not an equality. In fact, the preceding example shows that
$$
 \mathcal{I} \big(\mathbb{Z}[\{ 2, 3 \}] \big) \cup \mathcal{I} \big(\mathbb{Z}[\{1\}] \big) \neq \mathcal{I} \big(\mathbb{Z}[\J_3] \big).
$$}

\label{idempotens in trivial unions}
Let $X$ and $Y$ be trivial quandles of orders $n$ and $m$, respectively. Let $\mathbb{k}$ be an integral domain such that characteristic of $\mathbb{k}$ is coprime to both $n$ and $m$. Let $f \in Aut(X)$ and $g \in Aut(Y)$ be automorphisms acting transitively on $X$ and $Y$, respectively. Then 
\begin{small}
$$\mathcal{I}\big(\mathbb{k}[X \sqcup_{f, g} Y]\big)= \mathcal{I}\big(\mathbb{k}[X]\big) \sqcup~ \mathcal{I}\big(\mathbb{k}[Y]\big) \sqcup \Big\{\alpha \big(\sum_{x \in X} e_x \big) + \beta \big(\sum_{y \in Y} e_y \big) ~\bigl\vert~ \alpha, \beta \in \mathbb{k}~~\textrm{~such that~}~~\alpha n+ \beta m=1 \Big\}.$$
\end{small}

nodes.proof:
Note that any $u \in \mathbb{k}[X \sqcup_{f, g} Y]$ can be written uniquely as $u=v+w$, where $v= \sum_{x \in X} \alpha_x e_x \in \mathbb{k}[X]$, $w= \sum_{y \in Y} \beta_y e_y \in \mathbb{k}[Y]$ and $\alpha_x, \beta_y \in \mathbb{k}$. If $u=u^2$, then
$$v+w=v^2+w^2+vw+wv= \varepsilon(v)v + \varepsilon(w)w + \varepsilon(w) \sum_{x \in X} \alpha_x e_{f(x)}+ \varepsilon(v)\sum_{y \in Y} \beta_y e_{g(y)},$$
and consequently
$$v= \varepsilon(v)v + \varepsilon(w) \sum_{x \in X} \alpha_x e_{f(x)} \quad \textrm{and} \quad w= \varepsilon(w)w+ \varepsilon(v)\sum_{y \in Y} \beta_y e_{g(y)}.$$
Comparing coefficients give
\begin{equation}\label{eq union 1}
\alpha_x= \varepsilon(v) \alpha_x+ \varepsilon(w) \alpha_{f^{-1}(x)}
\end{equation}
and
\begin{equation}\label{eq union 2}
\beta_y = \varepsilon(w) \beta_y + \varepsilon(v) \beta_{g^{-1}(y)}
\end{equation}
for all $x\in X$ and $y \in Y$. Adding \eqref{eq union 1} for all $x \in X$ gives $\varepsilon(v)= \varepsilon(v) \varepsilon(u)$. Similarly, adding 
\eqref{eq union 2} for all $y \in Y$ gives $\varepsilon(w)= \varepsilon(w) \varepsilon(u)$. If $\varepsilon(u)=0$, then $\varepsilon(v)=\varepsilon(w)=0$, and hence $u=0$, a contradiction. So, we can assume that $\varepsilon(u)=1$, and hence at least one of $\varepsilon(v)$ or $\varepsilon(w)$ is non-zero. If $\varepsilon(v) \neq 0$, then \eqref{eq union 2} gives $\beta_y = \beta_{g^{-1}(y)}$ for all $y \in Y$. Since $g$ acts transitively on $Y$, it follows that $\beta_y= \beta$ (say) for all $y \in Y$. If $\beta=0$, then $w=0$. In this case, $u=\sum_{x \in X} \alpha_x e_x$, where $\sum_{x \in X} \alpha_x =1$, and hence $u \in  \mathcal{I}\big(\mathbb{k}[X]\big)$. If $\beta \neq 0$, then $\varepsilon(w) = m\beta \neq 0$, and \eqref{eq union 1} gives $\alpha_x= \alpha_{f^{-1}(x)}$ for all $x \in X$. Since $f$ also acts transitively on $X$, it follows that $\alpha_x= \alpha$ (say) for all $x \in X$. Thus, we have $$u= \alpha \big(\sum_{x \in X} e_x \big) + \beta \big(\sum_{y \in Y} e_y \big),$$ where $n \alpha +m \beta=1$. Similarly, if $\varepsilon(w) \neq 0$ and $\alpha = 0$, then we get $v=0$. In this case, $u=\sum_{y \in Y} \beta_y e_y$, where $\sum_{y \in Y} \beta_y =1$, and hence $u \in \mathcal{I}\big(\mathbb{k}[Y]\big)$. This completes the proof.  $\blacksquare$

