\cite[Theorem 6.10]{MR2166720}\label{colorextthm}
If a  coloring ${\mathcal C}$ of a link $L$ by $X$  lifts to a coloring of $L$ by the extension $E= A \times_{\alpha} X$, then the coloring ${\mathcal C}$ contributes  	a trivial term (that is, an integer in $\mathbb{Z}[A]$)
to  the generalized cocycle invariant $\Phi_{\kappa} (L) $.

\label{Theorem 1.4}
Let $\mathcal{O}$ be the set of orbits of a finite rack or a quandle $X$. Then the following assertions hold:
	\begin{enumerate}
		\item $\rank \big( \Ho_n^{\rm R}(X) \big)=|\mathcal{O}|^{n}$.
		\item $\rank \big(\Ho_n^{\rm Q}(X) \big)=|\mathcal{O}| \big(|\mathcal{O}|-1 \big)^{n-1}$.
		\item $\rank \big( \Ho_n^{\rm D}(X) \big)=|\mathcal{O}|^{n}-|\mathcal{O}| \big(|\mathcal{O}|-1 \big)^{n-1}$.
	\end{enumerate}

Let $\R_n$ be the dihedral quandle of order $n$. Then the following assertions hold:
\begin{enumerate}
\item $\Ho_3^{\rm Q}(\R_8) \cong \mathbb{Z}^2 \oplus \mathbb{Z}_8^2$.
\item $\Ho_4^{\rm Q}(\R_8) \cong \mathbb{Z}^2 \oplus \mathbb{Z}_2^4 \oplus \mathbb{Z}_4^4 \oplus \mathbb{Z}_8^2$.
\item $\Ho^{\rm Q}_3(\R_{16}) \cong \mathbb{Z}^2 \oplus \mathbb{Z}_{16}^2$.
\end{enumerate}

Let $m\geq 1$ be an integer.  A quandle $X$ is said to be \index{$m$-almost latin quandle}{\it $m$-almost latin} if it satisfies the following conditions:
\begin{enumerate}
\item The stabilizer $\Stab(a) =\{ x \in X | \; a*x=a \}$ has order $m$  for each $a \in X$.
\item The equation $a*x=b$ has a unique solution for each $b \in X \setminus \Stab(a)$.
	\end{enumerate}

{\rm The following are examples of $m$-almost latin quandles:
\begin{enumerate}
\item Every finite trivial quandle $X$ is an $|X|$-almost latin quandle.
\item The quandle $X$ of Example \ref{connected but not latin homology} is a $2$-almost latin quandle.
\item The subquandle of $\Conj(\Sigma_n)$ consisting of all $2$-cycles is a $\big(\binom{n-2}{2}+1\big)$-almost latin quandle if $n \geq 2$.
\item Let $(X,*)$ be a finite latin quandle and $\T_n$ the trivial quandle of order $n$. Consider the dynamical quandle 2-cocycle $\alpha : \T_n \times \T_n \to \Map(X\times X, X)$ defined by $\alpha_{a,b}(s,t)= s*t$ if $a=b$ and $\alpha_{a,b}(s,t)= s$ if $a\neq b$. Then the dynamical extension $X\times_{\alpha}\T_{n}$ of $\T_n$ by $X$ through $\alpha$ is a $\big((n-1)|X|+1\big)$-almost latin, but not connected for $n \geq 2$.  \cite[Example 2.5]{MR3571388}
\end{enumerate} }

Let $\R_{3}\times_{\alpha} \T_{2}$ be the quandle as above. Then the following assertions hold: 
\begin{enumerate}
\item $ \Ho_2^{\rm R}(\R_{3}\times_{\alpha} \T_{2}) \cong \mathbb{Z}^{4},\quad$ $\Ho_3^{\rm R}(\R_{3}\times_{\alpha} \T_{2}) \cong\mathbb{Z}^{8}\oplus\mathbb{Z}_{3}^{2}\quad$ and $\quad \Ho_4^{\rm R}(\R_{3}\times_{\alpha} \T_{2}) \cong\mathbb{Z}^{16}\oplus\mathbb{Z}_{3}^{8}$.
\item  $\Ho_2^{\rm Q}(\R_{3}\times_{\alpha} \T_{2})  \cong\mathbb{Z}^{2}, \quad$ $\Ho_3^{\rm Q}(\R_{3}\times_{\alpha} \T_{2}) \cong\mathbb{Z}^{2}\oplus\mathbb{Z}_{3}^{2}\quad$ and $\quad\Ho_4^{\rm Q}(\R_{3}\times_{\alpha} \T_{2}) \cong\mathbb{Z}^{2}\oplus\mathbb{Z}_{3}^{6}$.
\end{enumerate}

\label{prob7}
Let $X$ be an Alexander quandle such that $1-t$ invertible. Then there is an isomorphism
	\begin{equation*}\label{abc} \Ho_2^{\rm Q}(X;\mathbb{Z}) \cong X\wedge_{\mathbb{Z}} X / \langle x \wedge y - tx \wedge ty  \mid x,y \in X \rangle. 
	\end{equation*}

\label{prob5} 
Let $p$ be a prime and $n > 2$ an integer such that $\gcd(n, p)=1$.	Let $X$ be the Alexander quandle $\mathbb{F}_p [t]/(1+t+ \dots +t^{n-1})$. Then there is an isomorphism
	\begin{equation*}\label{abc34} \Ho_2^{\rm Q}(X;\mathbb{Z}) \cong (\mathbb{Z}_p)^{ \lfloor \frac{n-1}{2}\rfloor } .
	\end{equation*}

\label{Nosaka result on alexander quandles} 
Let $X=\mathbb{ Z}_p$ be the Alexander quandle of order $p$ with quandle operation $x*y=\omega x+(1-\omega)y,$ where $\omega \neq 0,1$.  Then
	$$\Ho^{\rm Q}_1(X) \cong \mathbb{ Z} \oplus \mathbb{ Z}_p^{b_1} \quad  \textrm{and} \quad \Ho^{\rm Q}_n(X) \cong  \mathbb{ Z}_p^{b_n}\quad  \textrm{for each} \quad n \geq 2,$$  where $b_{n+2e}=b_n+b_{n+1} + b_{n+2}$,  $b_1=b_2=\cdots =b_{2e-2}=0$,  $b_{2e-1}=b_{2e}=1$ and $e>0$ is the minimal integer satisfying $\omega^e=1$.

The quandle $3$-cocycle arising from $b_1 b_2 \in \Ho_{\rm{Grp}}^3(G; \mathbb{Z}_p)$ has the form
	\begin{equation}
		\label{eq:the_cocycle}
		(x,y,z) \mapsto 2z \big(d(y-x,z-y) + d(y-x,y-z) \big)
	\end{equation}
for all $x,y,z \in \R_p$. Further, it gives a non-trivial cohomology class in  $\Ho^3_{\rm Q}(\R_p; \mathbb{Z}_p)$.

If $p > 2$ is an odd integer, then the map $\R_p \times \R_p \times \R_p \to \mathbb{Z}_p$, defined by
	\begin{equation*}
		(x,y,z) \mapsto z \big(d(y-x,z-y) + d(y-x,y-z) \big),
	\end{equation*}
gives a non-trivial cohomology class in  $\Ho^3_{\rm Q}(\R_p; \mathbb{Z}_p)$.

Let $(X,f)$ be a permutation rack.  Let $\mathcal{O}$ be the set of all orbits of $(X,f)$ and $\mathcal{O}_{\rm{finite}}$ the subset of all finite orbits. Then the following assertions hold:
\begin{enumerate}
\item $\Ho^{\rm R}_0(X; \mathbb{Z}) \cong \mathbb{Z}$.
\item $\Ho^{\rm R}_1(X; \mathbb{Z}) \cong  \mathbb{Z}^\mathcal{O} \cong \mathbb{Z}^{|\mathcal{O}|}$.
\item  $\Ho^{\rm R}_2(X; \mathbb{Z}) \cong  \big(\mathbb{Z}^{\mathcal{O}}_0 \otimes \mathbb{Z}^\mathcal{O} \big) \oplus \mathbb{Z}^{\mathcal{O}_{\rm finite}} \cong \mathbb{Z}^{|\mathcal{O}| \big(|\mathcal{O}|-1 \big) + |\mathcal{O}_{\rm finite}|}$.
\end{enumerate}

\label{aut action on cocycles}
The map $\big(\Aut(X) \times \Sigma_S\big) \times \mathcal{Z}^2_{\rm Q}(X; S) \to \mathcal{Z}^2_{\rm Q}(X; S)$ given by
$$\big(( \phi, \theta), \alpha\big) \mapsto~ ^{(\phi, \theta)}\alpha$$ defines a left-action of the group $\Aut(X) \times \Sigma_S$ on the set $\mathcal{Z}^2_{\rm Q}(X; S)$.

\cite[Proposition 4.2]{MR4282648}
The map $\big(\Aut(X) \times \Sigma_S\big) \times \mathcal{H}^2_{\rm Q}(X; S) \to \mathcal{H}^2_{\rm Q}(X; S)$ given by
$$\big(( \phi, \theta), [\alpha] \big) \mapsto~ [^{(\phi, \theta)}\alpha]$$ defines a left-action of the group $\Aut(X) \times \Sigma_S$ on the set $\mathcal{H}^2_{\rm Q}(X; S)$.

\label{dynamical to ordinary cocycle}
{\rm 
Let $X$ be a quandle, $S=A$ a group (not necessarily abelian), and $\alpha: X \times X \to A$ a map. Then the map $\alpha': X \times X \to \Map(A \times A, A)$ given by $$\alpha'_{x, y}(s, t)= s ~\alpha_{x, y},$$ where $x, y \in X$ and $s, t \in A$, satisfy \eqref{dynamical-cocycle-condition1} and \eqref{dynamical-cocycle-condition3} if and only if $\alpha$ satisfy
\begin{equation}\label{group-coefficient-cocycle-condition1}
\alpha_{x, y}~\alpha_{x*y, z}= \alpha_{x, z}~\alpha_{x* z, y*z}
\end{equation}
and 
\begin{equation}\label{normalised-cocycle-condition}
\alpha_{x, x}=1
\end{equation}
for $x, y, z \in X$. If $A$ is abelian, then \eqref{group-coefficient-cocycle-condition1} is the cocycle condition defining the usual second quandle cohomology $\Ho^2_{\rm Q}(X; A)$ (see Section \ref{Homology} of Chapter  \ref{chapter homology YBE}).}

The following  assertions hold:
\begin{enumerate}
\item If $\psi \in \Aut(E)$ such that $\psi(x, s)=\big(\phi(x), \tau_x(s)\big)$, then $\tau_x \in \Sigma_S$.
\item $\Aut^{x_0}_S(E)$ is a subgroup of $\Aut(E)$.
\end{enumerate}

nodes.proof:
Assertion (1) follows from the bijectivity of $\psi$. For assertion (2), let $\psi \in \Aut^{x_0}_S(E)$ such that $\psi(x, s)=\big(\phi(x), \tau_x(s)\big)$ for all $x \in X$ and $s \in S$. Define $\varphi(x,s) = \big(\phi^{-1}(x), ~\tau_{\phi^{-1}(x)}^{-1}(s)\big)$. Since $\phi$ and $\tau_x$ are bijections for all $x \in X$, it follows that $\varphi$ is also a bijection. Further, since $\psi$ is a quandle homomorphism, for $x, y \in X$ and $s, t \in S$, we have
\begin{equation*}
\tau_{x*y}\big(\alpha_{x, y}(s, t)\big)=\alpha_{\phi(x), \phi(y)}\big(\tau_x(s),~ \tau_y(t)\big),
\end{equation*}
which is equivalent to
\begin{equation}\label{useful-in-subgroup}
\alpha_{\phi^{-1}(x), \phi^{-1}(y)} \big(\tau_{\phi^{-1}(x)}^{-1}(s),~ \tau_{\phi^{-1}(y)}^{-1}(t) \big)=\tau_{\phi^{-1}(x*y)}^{-1} \big(\alpha_{x, y}(s,t) \big).
\end{equation}

Now, we compute
\begin{eqnarray*}
\varphi \big( (x, s)*(y, t) \big) &=& \varphi \big(x*y,~ \alpha_{x, y}(s, t) \big)\\
 &=&  \big(\phi^{-1}(x*y), ~\tau_{\phi^{-1}(x*y)}^{-1}\big(\alpha_{x, y}(s, t) \big)\big)\\
  &=&  \big(\phi^{-1}(x)*\phi^{-1}(y), ~\alpha_{\phi^{-1}(x), \phi^{-1}(y)} \big(\tau_{\phi^{-1}(x)}^{-1}(s),~ \tau_{\phi^{-1}(y)}^{-1}(t)\big)\big),~\textrm{by}~\eqref{useful-in-subgroup}\\
  &=&    \big(\phi^{-1}(x), ~\tau_{\phi^{-1}(x)}^{-1}(s)\big)* \big(\phi^{-1}(y), ~\tau_{\phi^{-1}(y)}^{-1}(t)\big)\\
  &=&  \varphi(x,s) * \varphi(y,t).
\end{eqnarray*}
Thus, $\varphi \in \Aut^{x_0}_S(E)$ and a direct check shows that it is the inverse of $\psi$. Further, given $\psi(x, s)=\big(\phi(x), ~\tau_x(s)\big)$ and $\psi'(x, s)=\big(\phi'(x),~ \tau'_x(s)\big)$, we see that
\begin{equation}\label{product-psi}
\psi \psi'(x, s)=\psi \big( \phi'(x), ~\tau'_x(s)\big)=\big(\phi\big( \phi'(x)\big), ~\tau_{\phi'(x)}\big(\tau'_x(s)\big) \big)=\big(\phi \phi'(x), ~\tau_{\phi'(x)}\tau'_x(s) \big),
\end{equation}
and hence $\Aut^{x_0}_S(E)$ is a subgroup of $\Aut(E)$.
$\blacksquare$

If $\psi, \psi' \in \Aut^{x_0}_S(E)$, then by \eqref{product-psi}, we obtain 
$$\psi \psi'(x, s)=\big(\phi \phi'(x), ~\tau_{\phi'(x)}\tau'_x(s) \big),$$
where $\tau_{\phi'(x_0)}\tau'_{x_0}(s)=\tau_{x_0}\tau'_{x_0}(s)=\theta \theta'(s)$. This shows that $\Phi(\psi \psi' )=\Phi(\psi)\Phi(\psi' )$, that is, $\Phi$ is a homomorphism. Note that $$\Omega^{-1}\big([\alpha]\big)= \big( \Aut^{x_0}(X) \times \Sigma_S \big)_{[\alpha]},$$ the stabilizer of the action of $\Aut^{x_0}(X) \times \Sigma_S$ at $[\alpha]$. Let $\psi \in \Aut^{x_0}_S(E)$ such that $\Phi (\psi)= (\phi, \theta)$. Since $\psi$ is a quandle homomorphism, for $x, y \in X$ and $s, t \in S$, we have
\begin{equation}\label{ext-homo-condition1}
\tau_{x*y}\big(\alpha_{x, y}(s, t)\big)=\alpha_{\phi(x), \phi(y)}\big(\tau_x(s),~ \tau_y(t)\big).
\end{equation}
Further, since $\phi \in \Aut^{x_0}(X)$ and $\theta \in \Sigma_S$, we can replace $x, y$ by $\phi^{-1}(x),\phi^{-1}(y)$ and $s, t$ by $\theta^{-1}(s),\theta^{-1}(t)$, respectively,  in \eqref{ext-homo-condition1}. This gives
\begin{equation}\label{ext-homo-condition2}
\tau_{\phi^{-1}(x*y)}\Big(\alpha_{\phi^{-1}(x), \phi^{-1}(y)}\big(\theta^{-1}(s), ~\theta^{-1}(t)\big)\Big)=\alpha_{x, y}\Big(\tau_{\phi^{-1}(x)}\big(\theta^{-1}(s)\big), ~\tau_{\phi^{-1}(y)}\big(\theta^{-1}(t)\big)\Big).
\end{equation}
Define a map $\lambda: X \to \Sigma_S$ by setting $\lambda_x:=\theta \tau^{-1}_{\phi^{-1}(x)}$. Then, equation \eqref{ext-homo-condition2} takes the form
\begin{equation}\label{ext-homo-condition3}
\lambda_{x*y}^{-1} ~\theta \Big(\alpha_{\phi^{-1}(x), ~\phi^{-1}(y)}\big(\theta^{-1}(s), ~\theta^{-1}(t)\big)\Big)=\alpha_{x, y}\big(\lambda_x^{-1}(s), ~\lambda_y^{-1}(t) \big),
\end{equation}
which is further equivalent to
\begin{equation}\label{ext-homo-condition4}
^{(\phi, \theta)}\alpha_{x, y}(s, t)=\theta \Big(\alpha_{\phi^{-1}(x), ~\phi^{-1}(y)}\big(\theta^{-1}(s), ~\theta^{-1}(t)\big)\Big)= \lambda_{x*y} \Big(\alpha_{x, y}\big(\lambda_x^{-1}(s), ~\lambda_y^{-1}(t) \big) \Big).
\end{equation}
This shows that $\im(\Phi)\subseteq \big( \Aut^{x_0}(X) \times \Sigma_S \big)_{[\alpha]}$.
\para
For the converse, let $(\phi, \theta) \in \big( \Aut^{x_0}(X) \times \Sigma_S \big)_{[\alpha]}$. Then, there exists a map $\lambda: X \to \Sigma_S$ such that \eqref{ext-homo-condition4} holds. Define $\psi: E \to E$ by setting
$$\psi(x, s)= \big(\phi(x),~ \lambda_{\phi(x)}^{-1} \theta (s)\big).$$
Since $\phi$ and $\theta$ are bijective, so is $\psi$. By replacing $x, y$ by $\phi(x),\phi(y)$ and $s, t$ by $\theta(s),\theta(t)$, respectively, in \eqref{ext-homo-condition4}, we obtain
\begin{equation}\label{ext-homo-condition5}
\lambda_{\phi(x)*\phi(y)}^{-1}~\theta \big(\alpha_{x, y}(s, ~t)\big)=  \alpha_{\phi(x), \phi(y)}\big(\lambda_{\phi(x)}^{-1}\theta(s), ~\lambda_{\phi(y)}^{-1}\theta(t) \big).
\end{equation}
Now, we compute
\begin{eqnarray*}
\psi \big((x, s)*(y, t) \big) & = & \psi \big(x*y, ~\alpha_{x, y}(s, t) \big) \\
&  =&  \Big(\phi(x)*\phi(y), ~\lambda_{\phi(x)*\phi(y)}^{-1} \theta \big(\alpha_{x, y}(s, t)\big) \Big) \\
&  =&  \Big(\phi(x)*\phi(y), ~\alpha_{\phi(x), \phi(y)}\big(\lambda_{\phi(x)}^{-1}\theta(s), ~\lambda_{\phi(y)}^{-1}\theta(t) \big) \Big),~ \textrm{by}~\eqref{ext-homo-condition5}\\
&  =&  \big(\phi(x), ~\lambda_{\phi(x)}^{-1}\theta(s) \big)* \big(\phi(y), ~\lambda_{\phi(y)}^{-1}\theta(t) \big)\\ 
&  =& \psi (x, s)*\psi (y, t).
\end{eqnarray*}
Thus, $\psi \in\Aut^{x_0}_S(E)$ and $\Phi(\psi)=(\phi, \theta)$, which completes the proof of the proposition.
$\blacksquare$

