\label{prop03}
		Let $S$ be a numerical semigroup and $m\in S\setminus\{0\}$. Then 
		\[\mathrm{PF}(S)=\{w-m \mid w\in \mathrm{Maximals}_{\leq_S} (\mathrm{Ap}(S,m))  \}.\]

\label{cor06rar}
		For $n\geq 4$, let $S(n)$ be the numerical semigroup generated by the squares of the three consecutive Fibonacci numbers $f_n$, $f_{n+1}$, $f_{n+2}$.
		\begin{enumerate}
			\item If $n=6n_0+4$ or $n=6n_0+7$ with $n_0\in\mathbb{N}$ (equivalently, if $n=3k_0+1$ with $k_0\in\mathbb{N}\setminus\{0\}$), then
			\begin{itemize}
				\item $\gamma=4$,
				\item $\begin{pmatrix} s_v & P_v \\ s_{v+1} & P_{v+1} \end{pmatrix} =
				\begin{pmatrix} \frac{f_{n+2}}{2} & \frac{f_{n-1}}{2} \\ f_n & f_n \end{pmatrix}$,
				\item $\mathrm{PF}(S(n)) = \left\{ \left(\frac{f_{n+2}}{2}-1\right)f_{n+1}^2 + \left(\frac{f_{n-2}+f_n}{2}-1\right)f_{n+2}^2, \left( \frac{f_{n-1}}{2}-1\right)f_{n+1}^2 + (f_n-1)f_{n+2}^2 \right\} - f_n^2$.
			\end{itemize}

			\item If $n=6n_0+5$ or $n=6n_0+8$ with $n_0\in\mathbb{N}$ (equivalently, if $n=3k_0+2$ with $k_0\in\mathbb{N}\setminus\{0\}$), then
			\begin{itemize}
				\item $\gamma=3$,
				\item $\begin{pmatrix} s_v & P_v \\ s_{v+1} & P_{v+1} \end{pmatrix} =
				\begin{pmatrix} f_{n+2} & f_{n-1} \\ \frac{f_n+f_{n+2}}{2} & \frac{f_{n+1}}{2} \end{pmatrix}$,
				\item $\mathrm{PF}(S(n)) = \left\{ (f_{n+2}-1)f_{n+1}^2 + \left(\frac{f_{n-2}}{2}-1\right)f_{n+2}^2, \left(\frac{f_{n+1}}{2}-1\right)f_{n+1}^2 + \left(\frac{f_{n+1}}{2}-1\right)f_{n+2}^2 \right\} - f_n^2$.
			\end{itemize}

			\item If $n=6n_0+6$ or $n=6n_0+9$, with $n_0\in\mathbb{N}$ (equivalently, if $n=3k_0+3$ with $k_0\in\mathbb{N}\setminus\{0\}$), then
			\begin{itemize}
				\item $\gamma=1$,
				\item $\begin{pmatrix} s_v & P_v \\ s_{v+1} & P_{v+1} \end{pmatrix} =
				\begin{pmatrix} \frac{f_{n+3}}{2} & \frac{f_{n-3}}{2} \\ \frac{f_n}{2} & \frac{f_n}{2} \end{pmatrix}$,
				\item $\mathrm{PF}(S(n)) = \left\{ \left(\frac{f_{n+3}}{2}-1\right)f_{n+1}^2 + (f_{n-2}-1)f_{n+2}^2, (f_{n+1}-1)f_{n+1}^2 + \left(\frac{f_n}{2}-1\right)f_{n+2}^2 \right\} - f_n^2$.
			\end{itemize}
		\end{enumerate}
		Moreover, in any case, $\mathrm{F}(S(n)) = \max(\mathrm{PF}(S(n)))$.

\label{thm08tri5}
		Let $\ell > \kappa$ and $br > cq$. Let $u \equiv (a-\ell)\pmod{r}$.
		\begin{itemize}
			\item[(a)] If $\mu'< \left\lfloor \frac{r}{u} \right\rfloor$, then $\mathrm{F}(\langle a,b,c \rangle) =$ 
			\[ \max\left\{ b(r-\mu'u-1), b(u-1)+c\left(\mu' + \left( \left\lfloor \frac{(a-\ell)\mu'}{r} \right\rfloor+1 \right)q \right) \right\} + cq\left\lfloor \frac{a-\ell-1}{r} \right\rfloor -a. \]

			\item[(b)] Let $\mu'> \left\lfloor \frac{r}{u} \right\rfloor$. Let $d_1$, $d_2$ be the numbers given by Claim~\ref{claim05tri}, and let $p_i$ be the largest positive integer such that $x_{p_i}+d_i\in\mathcal{X}$ for $i=1,2$. Then
			\[ \mathrm{F}(\langle a,b,c \rangle) = \max\{b(d_1-1)+cy_{p_1}, b(d_2-1)+cy_{p_2} \} + cq\left\lfloor \frac{a-\ell-1}{r} \right\rfloor -a, \]
			where $y_{p_i} = q\left(\left\lfloor \frac{(a-\ell)p_i}{r} \right\rfloor +1\right) + p_i$ for $i=1,2$.
		\end{itemize}

\label{thm11tri6}
		Let $\ell > \kappa$ and $b(\ell-\overline{r}) > c(\overline{q}+1)$. Let $\overline{u} \equiv \overline{r}\pmod{(\ell-\overline{r})}$.
		\begin{itemize}
			\item[(a)] If $\overline{\mu}'< \left\lfloor \frac{\ell-\overline{r}}{\overline{u}} \right\rfloor$, then $\mathrm{F}(\langle a,b,c \rangle) =$ 
			\[ \max\left\{ b(\ell-\overline{r}-\overline{\mu}'\overline{u}-1), b(\overline{u}-1)+c\left( \left(\left\lfloor \frac{\ell\overline{\mu}'}{\ell-\overline{r}} \right\rfloor+1 \right)\overline{q} + \left\lfloor \frac{\overline{r}\overline{\mu}'}{\ell-\overline{r}} \right\rfloor+1 \right) \right\} + \]
			\[ c\left((\overline{q}+1)\left\lfloor \frac{\ell-1}{\ell-\overline{r}} \right\rfloor-2\right) -a. \]

			\item[(b)] Let $\overline{\mu}'> \left\lfloor \frac{\ell-\overline{r}}{\overline{u}} \right\rfloor$. Let $d_1$, $d_2$ be the numbers given by Claim~\ref{claim10tri}, and let $p_i$ be the largest positive integer such that $x_{p_i}+d_i\in\overline{\mathcal{X}}$ for $i=1,2$. Then
			\[ \mathrm{F}(\langle a,b,c \rangle) = \max\{b(d_1-1)+cy_{p_1}, b(d_2-1)+cy_{p_2} \} + c\left((\overline{q}+1)\left\lfloor \frac{\ell-1}{\ell-\overline{r}} \right\rfloor-2\right) -a, \]
			where $y_{p_i} = \overline{q}\left(\left\lfloor \frac{\ell p_i}{\ell-\overline{r}} \right\rfloor +1 \right) + \left\lfloor \frac{\overline{r} p_i}{\ell-\overline{r}} \right\rfloor+1$ for $i=1,2$.
		\end{itemize}

\label{lem12tri}
		If $n\geq 4$, then $\kappa = 2$ and
		\[ \ell = \begin{cases} f_nf_{n-3}+1 \mbox{ if $n$ is even,} \\[3pt] 2f_nf_{n-2}+1 \mbox{ if $n$ is odd.}
		\end{cases} \]

\label{cor14tri}
