			\item If $n=5$, then $br<cq$, and therefore we can apply Theorem~\ref{thm02tri3}.
			\item If $n\geq7$ is odd, then $br>cq$, and therefore we can apply Theorem~\ref{thm08tri5}.
		\end{itemize}

\label{cor17tri}
		It holds that:
		\begin{itemize}
			\item If $n\in\{4,6,8\}$, then $\mu'<\left\lfloor \frac{r}{u} \right\rfloor$, and therefore we can apply Theorem~\ref{thm08tri5}(a).
			\item If $n\geq 10$ is even, then $\mu'>\left\lfloor \frac{r}{u} \right\rfloor$, and therefore we can apply Theorem~\ref{thm08tri5}(b).
			\item If $n\in\{7,9,11\}$, then $\mu'<\left\lfloor \frac{r}{u} \right\rfloor$, and therefore we can apply Theorem~\ref{thm08tri5}(a).
			\item If $n\geq13$ is odd, then $\mu'>\left\lfloor \frac{r}{u} \right\rfloor$, and therefore we can apply Theorem~\ref{thm08tri5}(b).
		\end{itemize}

\label{cor20tri}
		Regarding Theorem~\ref{thm08tri5}(b), it holds that:
		\begin{enumerate}
			\item If $n=3k_0+1$ with $k_0\in\mathbb{N}\setminus\{0\}$, then
			\begin{itemize}
				\item $y_{p_1}=f_n-4$, $y_{p_2}=\frac{f_n+f_{n-2}}{2}-4$, if $n\geq10$ is even,
				\item $y_{p_1}=f_n-17$, $y_{p_2}=\frac{f_n+f_{n-2}}{2}-17$, if $n\geq13$ is odd,
				\item $\mathrm{PF}(S(n)) = \left\{ \left( \frac{f_{n-1}}{2}-1\right)f_{n+1}^2 + (f_n-1)f_{n+2}^2, \left(\frac{f_{n+2}}{2}-1\right)f_{n+1}^2 + \left(\frac{f_n+f_{n-2}}{2}-1\right)f_{n+2}^2 \right\} - f_n^2$.
			\end{itemize}

			\item If $n=3k_0+3$ with $k_0\in\mathbb{N}\setminus\{0\}$, then
			\begin{itemize}
				\item $y_{p_1}=\frac{f_n}{2}-4$, $y_{p_2}=f_{n-2}-4$, if $n\geq12$ is even,
				\item $y_{p_1}=\frac{f_n}{2}-17$, $y_{p_2}=f_{n-2}-17$, if $n\geq15$ is odd,
				\item $\mathrm{PF}(S(n)) = \left\{ (f_{n+1}-1)f_{n+1}^2 + \left(\frac{f_n}{2}-1\right)f_{n+2}^2, \left(\frac{f_{n+3}}{2}-1\right)f_{n+1}^2 + (f_{n-2}-1)f_{n+2}^2 \right\} - f_n^2$.
			\end{itemize}

			\item If $n=3k_0+2$ with $k_0\in\mathbb{N}\setminus\{0\}$, then
			\begin{itemize}
				\item $y_{p_1}=\frac{f_{n+1}}{2}-4$, $y_{p_2}=\frac{f_{n-2}}{2}-4$, if $n\geq14$ is even,
				\item $y_{p_1}=\frac{f_{n+1}}{2}-17$, $y_{p_2}=\frac{f_{n-2}}{2}-17$, if $n\geq17$ is odd,
				\item $\mathrm{PF}(S(n)) = \left\{ \left(\frac{f_{n+1}}{2}-1\right)f_{n+1}^2 + \left(\frac{f_{n+1}}{2}-1\right)f_{n+2}^2, (f_{n+2}-1)f_{n+1}^2 + \left(\frac{f_{n-2}}{2}-1\right)f_{n+2}^2 \right\} - f_n^2$.
			\end{itemize}
		\end{enumerate}
		Moreover, in any case, $\mathrm{F}(S(n)) = \max(\mathrm{PF}(S(n)))$.

