		If $n\geq8$, then
		\begin{enumerate}
			\item $f_{n+4}f_n^2 = f_{n+1}f_{n+1}^2 + f_{n-2}f_{n+2}^2$.
			\item $f_{n+2}f_{n+1}^2 = f_{n+2}f_n^2 + f_{n-1}f_{n+2}^2$.
			\item $f_n f_{n+2}^2 = f_{n+3} f_n^2 + f_n f_{n+1}^2$.
		\end{enumerate}

\label{lem14rgs}
		If $f_n$ is even (that is, $n=3k$ with $k\in\mathbb{N}\setminus\{0,1\}$), then
		\begin{enumerate}
			\item $f_{n+4}f_n^2 = f_{n+1}f_{n+1}^2 + f_{n-2}f_{n+2}^2$.
			\item $\frac{f_{n+3}}{2}f_{n+1}^2 = \frac{f_{n+2}+f_{n+4}}{2} f_n^2 + \frac{f_{n-3}}{2}f_{n+2}^2$.
			\item $\frac{f_n}{2}f_{n+2}^2 = \frac{f_{n+3}}{2}f_n^2 + \frac{f_n}{2}f_{n+1}^2$.
		\end{enumerate}

\label{exmp16rgs}
		For $n=6$, we have that $S(6)=\langle 64,169,441 \rangle$ and, by applying Proposition~\ref{prop15rgs},
		\[ \mathrm{Ap}(S(6),64) = \big\{ 169\lambda + 441\mu \mid (\lambda,\mu)\in \{0,\dots,16\} \times \{0,1,2,3\} \setminus \{13,14,15,16\} \times \{3\} \big\}. \]

\label{lem11rgs}
		If $f_{n+1}$ is even (that is, if $n=3k-1$ with $k\in\mathbb{N}\setminus\{0,1\}$), then
		\begin{enumerate}
			\item $\frac{f_{n+4}}{2}f_n^2 = \frac{f_{n+1}}{2}f_{n+1}^2 + \frac{f_{n-2}}{2}f_{n+2}^2$.
			\item $f_{n+2}f_{n+1}^2 = f_{n+2}f_n^2 + f_{n-1}f_{n+2}^2$.
			\item $\frac{f_{n+1}}{2}f_{n+2}^2 = \frac{f_{n+1}}{2}f_n^2 + \frac{f_n+f_{n+2}}{2}f_{n+1}^2$.
		\end{enumerate}

\label{exmp13rgs}
		For $n=5$, we have that $S(5)=\langle 25,64,169 \rangle$ and, by applying Proposition~\ref{prop12rgs}, we conclude that
