[{\cite[Prop 7.15]{knapp1986representation}}]\label{lem: W_G invariance, spherical functions}
    For any $\chi\in\alie^*$ and $w\in W_G$, we have \begin{equation*}
        \Xi_{\chi}^G = \Xi^G_{w\chi}.
        \qedineq
    \end{equation*}

[{\cite[Thm 3.4]{narayanan-pasquale2014asymptotics}}]\label{lem: spherical functions}
    For each $\chi\in (\alie^*)^+$, there exists a polynomial $p(\cdot)$ on $\alie$ such that for all $g\in G$, we have
    \begin{equation*}
        \bigexp{(\chi-\rho)\kappa(g)} \leq \Xi^G_{\chi}(g) \leq p(\kappa(g))\bigexp{(\chi-\rho)\kappa(g)}.
        \qedineq
    \end{equation*}

[{\cite[Lem 4.1]{benoist-kobayashi2022tempered2}}]\label{lem: nice intermediate subgroups}
    There exist two intermediate algebraic subgroups $H\subset F\subset Q\subset G$ with the following properties:
    \begin{enumerate}
        \item $Q$ is a parabolic subgroup of $G$ of minimal dimension containing $H$.
        \item Let $U$ be the unipotent radical of $Q$. There exist a Levi decomposition $Q = LU$ such that $L$ is a maximal reductive subgroup of $Q$ and that $H = (L\cap H)(U\cap H)$.
        \item $S=L\cap H$ is a maximal reductive subgroup of $H$ and $W=U\cap H$ is the unipotent radical of $H$.
        \item $F= SU$.
    \end{enumerate}
    That is to say, we have a chain of algebraic subgroups with compatible Levi decompositions
    \begin{equation*}
        H=SW \subset F=SU \subset Q= LU \subset G.
        \qedineq
    \end{equation*}

[{\cite[Prop 4.4]{benoist-kobayashi2022tempered2}}]\label{lem: domination of group action}
    Let $U/W$ be equipped with a $U$-invariant Radon measure $\Vol$.
    Then for every compact subset $D\subset U/W$, there exists a compact subset $D_0\subset U/W$ such that for all $s\in S$ and $u\in U$, we have
    \begin{equation*}
        \Vol(suD\cap D) \leq \Vol(sD_0\cap D_0).
        \qedineq
    \end{equation*}

\label{prop: domination of Q/H}
    For any $f_1, f_2\in \Cc(Q/H)$, there exist $\varphi_1, \varphi_2\in \Cc(L)$ and $D_1, D_2 \Subset U/W$ such that
    \begin{equation*}
        \Modofinner{\lambda_{Q/H}(q)f_1, f_2} \leq \Innerprod{\pi_0(l_q)\rmI_S^L(\varphi_1, \indicator_{D_1}), \rmI_S^L(\varphi_2,\indicator_{D_2})}
    \end{equation*}
    uniformly for all $q\in Q$, where $l_q$ is the $L$-component of $q$.
    \qed

\label{prop: uniform decay of reductive induction}
    Given any $\varphi_1,\varphi_2\in \Cc(L)$ and $D_1, D_2 \Subset V$, we form $f_i = \rmI_S^L(\varphi_i, \indicator_{D_i})$ for $i=1,2$. Then there exists a constant $C>0$ such that
