\label{ExPlus}
\begin{enumerate}
\item The augmentation ideal $J_+$ is the identity element for the operation $\Join$, meaning that for every ideal $I$ in $\bk S_\infty$,
$$I\Join J_+\;=\; I\;=\; J_+\Join I.$$
\item The identity ideal is the zero element for the operation $\Join$, meaning 
$$I\Join \bk S_\infty\;=\; \bk S_\infty\;=\; \bk S_\infty\Join I.$$
\item For two $\bk S_\infty$-modules, we have
$$\Ann (M\otimes N)\;=\; \Ann(M)\Join \Ann(N),$$
for example $J_-\Join J_-=J_+$.
\end{enumerate}

\label{ExDagger}
\begin{enumerate}
\item The identity ideal $\bk S_\infty$ is the identity element for the operation  $\dagger$, meaning
$$I\dagger \bk S_\infty\;=\; I\;=\; \bk S_\infty \dagger I.$$
\item For $m,n\in\mN$, we define the ideal 
$$\mP_{m,n}\,:= \,J_+^{\dagger m}\dagger J_-^{\dagger n},$$
so $\mP_{0,0}=\bk S_\infty$ by convention. Then $(\mP_{m,n})_i$ is the kernel of the permutation action
$$\bk S_i\;\to\; \End(V^{\otimes i}),$$
for $V$ a supervector space of dimension $m|n$. This follows directly, or from Example~\ref{ExsVec} below. We take the convention that for $\mathrm{char}(\bk)=2$, we have $\mP_{m,n}=J^{\dagger (m+n)}_+$.
\end{enumerate}

\label{LemdAdd}
For non-zero ideals $I,J$ in $\bk S_\infty$, we have
$$\ddd(I\dagger J)\;\ge\; \ddd(I)+\ddd(J).$$

\label{Lemgdagger}
For $I,J\in \TSpec\bk S_\infty$, we have
\begin{enumerate}
\item $\max\{\gggr(I),\gggr(J)\}\;\le\; \gggr(I\dagger J)\;\le\; \gggr(I)+\gggr(J)$;
\item $\gggr(I\Join J)\;\le\; \gggr(I)\gggr(J)$.
\end{enumerate}

\label{Lemmag}
\begin{enumerate}
\item We have $\gggr(\mP_{m,0})=\gggr(J_+^{m\dagger})=m$.
\item If $\mathrm{char}(\bk)=0$, then $\gggr(\mP_{m,n})=m+n$.
\end{enumerate}

\label{Prop:0}
\begin{enumerate}
\item There is an inclusion order reversing bijection from $\bP_\infty$ to $\Spec \bk S_\infty$, given by $\lambda\mapsto I_\lambda$, where for all $n\in\mZ_{>0}$
$$I_\lambda\cap \bk S_n\;=\; \bigcap_{\mu\vdash n\mid \mu\subset \lambda}\Ann(D^\mu).$$
\item Every ideal in $\bk S_\infty$ is a finite intersections of prime ideals.
 \end{enumerate}

\label{Cor:Lem}
For $\lambda\in\bP_\infty$, let $\Phi_\lambda$ be the inductive system corresponding to all $D^\mu$ with $\mu\subset\lambda$. Every union
$\cup_{\lambda\in S}\Phi_\lambda$ is equal to $\cup_{\lambda\in S_0}\Phi_\lambda$ for a finite subset $S_0\subset S$.

\label{ThmClassT}
The following conditions are equivalent on a prime ideal $I<\bk S_\infty$.
\begin{enumerate}
\item $I$ is T-prime;
\item $I$ does not have a cover in the inclusion order on $\Spec \bk S_\infty$;
\item $I$ is zero or $I=\mP_{m,n}$ for $(m,n)\in \mN\times\mN$;
\item $I$ is not the sum of two other ideals;
\item $I=\TAnn(X)$ for some object $X$ in a tensor category $\cC$ over $\bk$.
\end{enumerate}

\label{LemSub}
We have 
$$\LAnn V(\omega,\Lambda,M)\;\subset\;\LAnn T^\omega\;\supset\; \RAnn V(\omega,\Lambda,M).$$

\begin{enumerate}
\item A consequence of the conjecture would be that classifying (T-)prime ideals in $\bk S_\infty$ is a wild problem.
\item Conjecture~\ref{ConjVer} is a stronger version of \cite[Conjecture~1.4]{BEO}. Indeed, assume that Conjecture~\ref{ConjVer} is true and let $\cC$ be an arbitrary incompressible tensor category. By \cite[Corollary~5.2.8]{Incomp}, there exists an incompressible tensor category $\cD$ containing both $\Ver_{p^\infty}$ and $\cC$ as tensor subcategories. Then $\TAnn_{\Ver_{p^\infty}}$ factors via $\TAnn_{\cD}$. However, under Conjecture~\ref{ConjVer}, the former is an isomorphism, while the latter is injective, implying that $M^{\oplus}(\Ver_{p^\infty})\hookrightarrow M^{\oplus}(\cD)$ is an isomorphism, forcing $\cD=\Ver_{p^\infty}$ and hence $\cC\subset \Ver_{p^\infty}$. The conclusion follows from \cite[Theorem~A]{Incomp}.
\end{enumerate}

\label{ThmVer}
\begin{enumerate}
\item The annihilator map $\TAnn_{\Ver_4}$
is injective.
\item For every prime $p$, the annihilator map
$\TAnn_{\Ver_p}$
is injective.
\end{enumerate}

\label{ThmFinal}
\begin{enumerate}
\item A non-zero proper ideal $I<\bk S_\infty$ admits a dimension if and only if it is of the form $\TAnn_{\cA}(X)$ for an object $X$ in a RSM category $\cA$. The dimension of $I$ then equals the categorical dimension of $X$.
\item For every $\delta\in\bk$, there is a bijection between the non-zero proper tensor ideals in $\OB(\delta)$ and the proper non-zero ideals in $\bk S_\infty$ admitting dimension $\delta$, given by $\cI\mapsto \TAnn_{\OB(\delta)/\cI}(V_\delta)$.
\item If $\mathrm{char}(\bk)=0$, then a non-zero proper ideal $I<\bk S_\infty$ admits a dimension if and only if it is T-prime. Moreover, the dimension of $\mP_{m,n}$ is $m-n$.
\item If $\mathrm{char}(\bk)>3$, there are ideals in $\bk S_\infty$ that admit a dimension, but are not T-prime.
\end{enumerate}

\label{CorFin}
\begin{enumerate}
\item If a skew symmetriser vanishes on an object in a RSM category, then its categorical dimension corresponds to the last one that does not vanish.
\item If $\mathrm{char}(\bk)=p>0$, then $\OB(\delta)$ only has non-zero proper tensor ideals if $\delta\in \mF_p$.
\end{enumerate}
