We call a $k$-coloured word $ \mathbf w $ representing $ g \in \raag \Gamma $:
	\begin{itemize}
		\item \emph{geodesic}, if it is a minimal-length representative for $ g $;
		\item \emph{chromatically minimal}, if there are no $k'$-coloured words representing $g$ with $k'<k$;
		\item \emph{efficient}, if it is geodesic and every proper block is chromatically minimal.
	\end{itemize}

\label{bound-colour-distance-via-corridors}
	Let $w$ be a null-homotopic word in $\raaggens$ and $D$ a van Kampen diagram for $w$. Let $p$ and $q$ be two vertices on the boundary of $D$, and let $g$ be the element represented by the word read from $p$ to $q$ along the boundary. Let $ C $ and $ C' $ be corridors containing $ p $ and $q$, respectively.
	\begin{enumerate}
		\item\label{item: 2-coloured represented} If $ C $ and $ C' $ cross, then $g$ can be represented by a $2$-coloured word.
		\item\label{item: 3-coloured represented} If there is a corridor that crosses both $ C$ and $C' $, then $g$ can be represented by a $3$-coloured word.
	\end{enumerate}

\label{estimate-sum-of-corridors-length}
	Let $\mathbf w = \colword{w_1}{a_1} \cdots \colword{w_k}{a_k}$ be an efficient $k$-coloured word whose underlying word is $w=w_1 \cdots w_k$. Let $ w'$ be a word in $\raag \Gamma$ such that $w w'$ is null-homotopic, and let $D$ be a minimal-area van Kampen diagram for $w w'$. For $i \in \range{k-1}$, let $p_i$ be the vertex on the boundary of $D$ at the intersection of $w_i$ and $w_{i+1}$, and let $C_i$ be a corridor containing $p_i$. Then, for $i,j \in \range {k-1}$, the following statements hold.
	\begin{enumerate}
		\item\label{item:one-corridors} If $ \abs{i-j} \geq 3 $, then $ C_i $ and $ C_j $ do not cross.
		\item\label{item:two-corridors} If $ \abs{i-j} \geq 4 $, no corridor crosses both $ C_i $ and $ C_j $.
		\item\label{item:three-corridors} The total length of the corridors $C_i$ satisfies
		\[
			\sum_i \abs{C_i} \leq 2 \length{w w'}.
		\]
	\end{enumerate}

\label{sparse-pushdown}
    For every $M > 0$, there exists $C_M > 0$ such that the following holds.    
	Let $\mathbf D$ be a coloured diagram, and for every polychromatic vertex $p$, let $\gamma_p$ be a path in $\bolddiag$ from $p$ to an arbitrary $\partial$-polychromatic vertex (if $p$ is $\partial$-polychromatic, then we can choose $\gamma_p$ to be the constant path). Suppose that
	\begin{enumerate}
		\item for every polychromatic vertex $p$ in $\bolddiag$, we have $\deg p \leq M$, and \label{item:bound-degree}
		\item for every edge $e$ in $\bolddiag$, we have $\cardinality{ \set{ \gamma_p \mid e \in \gamma_p} } \leq M$, and \label{item:bound-paths-through-edge}
		\item $\density \bolddiag \leq M$.\label{item:bound-density-coloured-diagram}
	\end{enumerate}
Then $\density{\pushdown[h]{\mathbf D}} \leq C_M$ for every $h \in \Z$.

